import React, { useState, useEffect } from 'react';
import { Plus, Minus } from 'lucide-react';

const FAQComponent = () => {
  const [expandedIndex, setExpandedIndex] = useState(-1);
  const [isComponentVisible, setIsComponentVisible] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsComponentVisible(true);
    }, 500);

    return () => clearTimeout(timer);
  }, []);

  const toggleExpand = (index) => {
    setExpandedIndex(index === expandedIndex ? -1 : index);
  };

  const faqData = [
    { 
      question: 'Beşirağa Külliyesi hangi yıllarda yapılmıştır?', 
      answer: 'Beşirağa Külliyesi, Hacı Beşir Ağa tarafından 1744-1745 yıllarında (Hicri 1157-1158) yaptırılmıştır. İnşası en az iki yıl sürmüştür.' 
    },
    { 
      question: 'Külliye hangi yapılardan oluşmaktadır?', 
      answer: 'Külliye; cami, kütüphane, sebil, tekke, sıbyan mektebi ve medreseden oluşan kapsamlı bir yapı topluluğudur. Her biri Osmanlı mimarisinin önemli örneklerindendir.' 
    },
    { 
      question: 'Sesli tur nasıl kullanılır?', 
      answer: 'Sesli tur özelliği ile külliyenin her noktasında detaylı bilgi alabilirsiniz. Kulaklık takarak gezinti boyunca otomatik anlatımları dinleyebilirsiniz.' 
    },
    { 
      question: 'Beşirağa Sebili neden önemlidir?', 
      answer: 'Beşirağa Sebili, İstanbul sebilleri arasında barok üslubunun ilk uygulandığı ve değişik biçimde bir örnek olarak sanat tarihi bakımından özel bir yere sahiptir. Pencereleri içeri kavisli olması ile benzersizdir.' 
    },
    { 
      question: 'Ziyaret saatleri nedir?', 
      answer: 'Külliye günün belirli saatlerinde ziyarete açıktır. Detaylı bilgi için lütfen resmi kaynaklara başvurunuz veya bizimle iletişime geçiniz.' 
    },
  ];

  return (
    <div className="max-w-full h-[60rem] flex items-center justify-center flex-col px-5 sm:px-10 py-10 font-['Arial',sans-serif] font-bold overflow-hidden">
      <h2 
        className="text-center mb-5 text-white text-4xl sm:text-5xl font-bold opacity-0 -translate-y-12"
        style={{
          animation: isComponentVisible ? 'fadeInDown 0.5s ease-out forwards' : 'none'
        }}
      >
        Sık Sorulan Sorular
      </h2>

      {faqData.map((item, index) => (
        <div
          key={index}
          className={`mx-auto w-full sm:w-[90%] md:w-[80%] lg:w-[60%] rounded mb-5 overflow-hidden text-white text-xl sm:text-2xl md:text-[28px] py-2 opacity-0 ${
            expandedIndex === index ? 'shadow-[0_4px_6px_rgba(0,0,0,0.9)]' : ''
          }`}
          style={{
            animation: isComponentVisible 
              ? index % 2 === 0 
                ? `slideInFromLeft 0.5s ease-out ${index * 0.1}s forwards`
                : `slideInFromRight 0.5s ease-out ${index * 0.1}s forwards`
              : 'none'
          }}
        >
          <div 
            className="flex justify-between items-center px-4 sm:px-6 py-4 sm:py-6 bg-amber-900 transition-all duration-300 cursor-pointer hover:bg-amber-950 hover:scale-[1.02]"
            onClick={() => toggleExpand(index)}
          >
            <h3 className="text-base sm:text-lg font-medium m-0">
              {item.question}
            </h3>
            <div className="flex justify-center items-center w-8 h-8 rounded-full bg-stone-800 transition-colors duration-300 hover:bg-stone-700">
              {expandedIndex === index ? (
                <Minus size={20} className="text-amber-500" />
              ) : (
                <Plus size={20} className="text-amber-500" />
              )}
            </div>
          </div>

          {expandedIndex === index && (
            <div 
              className="px-4 sm:px-6 py-4 text-sm sm:text-base leading-relaxed opacity-0 bg-amber-900/80"
              style={{
                animation: 'fadeIn 0.3s ease-out forwards'
              }}
            >
              <p className="m-0 text-amber-100">{item.answer}</p>
            </div>
          )}
        </div>
      ))}

      <style jsx>{`
        @keyframes slideInFromLeft {
          0% {
            transform: translateX(-100%);
            opacity: 0;
          }
          100% {
            transform: translateX(0);
            opacity: 1;
          }
        }

        @keyframes slideInFromRight {
          0% {
            transform: translateX(100%);
            opacity: 0;
          }
          100% {
            transform: translateX(0);
            opacity: 1;
          }
        }

        @keyframes fadeIn {
          0% {
            opacity: 0;
          }
          100% {
            opacity: 1;
          }
        }

        @keyframes fadeInDown {
          0% {
            opacity: 0;
            transform: translateY(-50px);
          }
          100% {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </div>
  );
};

export default FAQComponent;
