// --- STİL FONKSİYONU ---
// Butonların durumuna göre (doğru, yanlış, seçili, vb.) stillerini belirler.
const getOptionClasses = (option, selectedOption, isAnswered) => {
  const baseClasses = "w-full text-left p-4 rounded-lg border-2 transition-all duration-300 text-lg font-medium";

  if (!isAnswered) {
    return `${baseClasses} border-amber-200 text-amber-900 bg-white hover:bg-amber-50 hover:border-amber-400 cursor-pointer`;
  }

  // Cevap verildiğinde
  if (option.isCorrect) {
    return `${baseClasses} border-emerald-500 bg-emerald-500 text-white shadow-lg scale-105`;
  }

  if (option === selectedOption && !option.isCorrect) {
    return `${baseClasses} border-red-500 bg-red-500 text-white shadow-lg scale-105`;
  }

  return `${baseClasses} border-gray-200 bg-gray-100 text-gray-400`;
};


// --- ANA REACT BİLEŞENİ ---
export default function BilgiYarismasi() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [showResults, setShowResults] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null); // Seçilen 'option' objesini tutar
  const [isAnswered, setIsAnswered] = useState(false);

  const totalQuestions = quizData.length;
  const currentQuestion = quizData[currentQuestionIndex];

  // Bir seçenek seçildiğinde
  const handleOptionClick = (option) => {
    if (isAnswered) return; // Zaten cevaplandıysa bir şey yapma

    setSelectedOption(option);
    setIsAnswered(true);

    if (option.isCorrect) {
      setScore(prevScore => prevScore + 1);
    }
  };

  // Sonraki soruya geçiş
  const handleNextQuestion = () => {
    if (currentQuestionIndex < totalQuestions - 1) {
      setCurrentQuestionIndex(prevIndex => prevIndex + 1);
      setSelectedOption(null);
      setIsAnswered(false);
    } else {
      setShowResults(true);
    }
  };

  // Testi yeniden başlat
  const handleRestartQuiz = () => {
    setCurrentQuestionIndex(0);
    setScore(0);
    setShowResults(false);
    setSelectedOption(null);
    setIsAnswered(false);
  };

  // --- RENDER ---
  return (
    <div className="w-full min-h-screen bg-gradient-to-b from-amber-50 to-stone-50 py-16 px-4">
      <div className="max-w-3xl mx-auto bg-white rounded-2xl shadow-xl overflow-hidden border border-amber-200/50">

        {/* SONUÇ EKRANI */}
        {showResults ? (
          <div className="p-8 md:p-12 text-center">
            <Trophy className="w-20 h-20 text-yellow-500 mx-auto mb-6" />
            <h2 className="text-4xl font-bold text-amber-900 mb-4">Test Tamamlandı!</h2>
            <p className="text-2xl text-gray-700 mb-8">
              Skorunuz: <span className="font-bold text-amber-800">{score} / {totalQuestions}</span>
            </p>
            <div className="w-full bg-gray-200 rounded-full h-4 mb-8">
              <div
                className="bg-gradient-to-r from-amber-500 to-yellow-500 h-4 rounded-full transition-all duration-500"
                style={{ width: `${(score / totalQuestions) * 100}%` }}
              ></div>
            </div>
            <button
              onClick={handleRestartQuiz}
              className="flex items-center justify-center gap-2 mx-auto px-8 py-3 bg-amber-800 text-white rounded-lg font-bold text-lg hover:bg-amber-700 transition-colors"
            >
              <RotateCcw size={20} />
              <span>Yeniden Başlat</span>
            </button>
          </div>
        ) : (

        /* SORU EKRANI */
        <>
          {/* Üst Bilgi ve İlerleme Çubuğu */}
          <div className="p-6 md:p-8 border-b border-gray-200">
            <div className="flex justify-between items-center mb-4">
              <span className="text-xl font-bold text-amber-800">Soru {currentQuestionIndex + 1}
                <span className="text-gray-400 font-normal"> / {totalQuestions}</span>
              </span>
              <span className="text-lg font-bold text-gray-600">Puan: {score}</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2.5">
              <div
                className="bg-amber-600 h-2.5 rounded-full transition-all duration-300"
                style={{ width: `${((currentQuestionIndex + 1) / totalQuestions) * 100}%` }}
              ></div>
            </div>
          </div>

          {/* Soru ve Seçenekler */}
          <div className="p-6 md:p-8">
            <h2 className="text-2xl md:text-3xl font-bold text-amber-900 mb-8 leading-snug">
              {currentQuestion.question}
            </h2>

            <div className="space-y-4">
              {currentQuestion.options.map((option) => (
                <button
                  key={option.text}
                  onClick={() => handleOptionClick(option)}
                  disabled={isAnswered}
                  className={getOptionClasses(option, selectedOption, isAnswered)}
                >
                  {option.text}
                </button>
              ))}
            </div>

            {/* Cevap Geri Bildirimi */}
            {isAnswered && (
              <div className={`mt-8 p-5 rounded-lg text-white animate-fade-in ${selectedOption.isCorrect ? 'bg-emerald-600' : 'bg-red-600'}`}>
                <div className="flex items-center gap-3 mb-2">
                  {selectedOption.isCorrect ? <CheckCircle size={24} /> : <XCircle size={24} />}
                  <h3 className="text-2xl font-bold">{selectedOption.isCorrect ? 'Doğru!' : 'Yanlış.'}</h3>
                </div>
                <p className="text-lg opacity-90">{selectedOption.rationale}</p>
              </div>
            )}

            {/* Sonraki Butonu */}
            {isAnswered && (
              <div className="mt-8 text-right">
                <button
                  onClick={handleNextQuestion}
                  className="flex items-center justify-center gap-2 ml-auto px-8 py-3 bg-amber-800 text-white rounded-lg font-bold text-lg hover:bg-amber-700 transition-colors"
                >
                  <span>
                    {currentQuestionIndex < totalQuestions - 1 ? 'Sonraki Soru' : 'Sonuçları Gör'}
                  </span>
                  <ArrowRight size={20} />
                </button>
              </div>
            )}
          </div>
        </>
        )}
      </div>
    </div>
  );
}

