import React, { useEffect, useState, useRef } from 'react';
import { Play, X, Globe } from 'lucide-react';

const PodcastTransition = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [scrollProgress, setScrollProgress] = useState(0);
  const [showLanguageModal, setShowLanguageModal] = useState(false);
  const sectionRef = useRef(null);

  const languages = [
    { code: 'tr', name: 'T√ºrk√ße', flag: 'üáπüá∑' },
    { code: 'en', name: 'English', flag: 'üá¨üáß' },
    { code: 'ar', name: 'ÿßŸÑÿπÿ±ÿ®Ÿäÿ©', flag: 'üá∏üá¶' }
  ];

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        setIsVisible(entry.isIntersecting);
      },
      { 
        threshold: [0, 0.5, 1],
        rootMargin: "-10% 0px -10% 0px"
      }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => observer.disconnect();
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      if (!sectionRef.current) return;

      const rect = sectionRef.current.getBoundingClientRect();
      const viewportHeight = window.innerHeight;
      const elementMiddle = rect.top + rect.height / 2;
      const viewportMiddle = viewportHeight / 2;
      const distance = Math.abs(elementMiddle - viewportMiddle);
      const maxDistance = (viewportHeight + rect.height) / 2;

      let progress = 1 - (distance / maxDistance);
      progress = Math.min(Math.max(progress, 0), 1);

      setScrollProgress(progress);
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll();
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleLanguageSelect = (langCode) => {
    console.log(`Selected language: ${langCode}`);
    // Burada podcast √ßalma fonksiyonunu ekleyebilirsiniz
    setShowLanguageModal(false);
  };

  return (
    <>
      <section 
        ref={sectionRef} 
        className="w-full min-h-screen flex items-center justify-center relative overflow-hidden bg-gradient-to-b from-stone-50 to-stone-100"
      >
        {/* Animated Background Gradient */}
        <div 
          className="absolute inset-0 transition-all duration-1000 ease-out"
          style={{
            background: `linear-gradient(135deg, 
              rgba(209, 42, 44, ${scrollProgress * 0.05}) 0%, 
              rgba(139, 28, 30, ${scrollProgress * 0.08}) 50%, 
              rgba(70, 14, 15, ${scrollProgress * 0.1}) 100%)`,
          }}
        />

        {/* Subtle Pattern Overlay */}
        <div 
          className="absolute inset-0 opacity-[0.02]"
          style={{
            backgroundImage: `radial-gradient(circle at 2px 2px, rgba(209, 42, 44, 0.15) 1px, transparent 0)`,
            backgroundSize: '48px 48px',
            transform: `translateY(${scrollProgress * -20}px)`,
            transition: 'transform 0.6s ease-out'
          }}
        />

        {/* Main Content Container */}
        <div className="relative z-10 flex flex-col items-center justify-center px-4 sm:px-6 lg:px-8 max-w-4xl mx-auto text-center">

          {/* Decorative Top Line */}
          <div 
            className="w-16 sm:w-20 h-0.5 bg-gradient-to-r from-transparent via-[#D12A2C] to-transparent mb-8 sm:mb-12"
            style={{
              opacity: scrollProgress,
              transform: `scaleX(${scrollProgress})`,
              transition: 'all 0.8s ease-out'
            }}
          />

          {/* Title */}
          <h2 
            className="text-2xl sm:text-3xl md:text-4xl lg:text-5xl font-bold text-gray-900 mb-4 sm:mb-6 font-['Maven_Pro',sans-serif]"
            style={{
              opacity: scrollProgress,
              transform: `translateY(${(1 - scrollProgress) * 30}px)`,
              transition: 'all 0.8s ease-out'
            }}
          >
            Sesli Rehber
          </h2>

          {/* Subtitle */}
          <p 
            className="text-sm sm:text-base md:text-lg text-gray-600 mb-12 sm:mb-16 max-w-2xl leading-relaxed font-['Inter',sans-serif]"
            style={{
              opacity: scrollProgress,
              transform: `translateY(${(1 - scrollProgress) * 20}px)`,
              transition: 'all 0.8s ease-out 0.1s'
            }}
          >
            Be≈üiraƒüa K√ºlliyesi'nin b√ºy√ºleyici hikayesini sesli rehberimiz e≈üliƒüinde ke≈üfedin
          </p>

          {/* Play Button */}
          <button
            onClick={() => setShowLanguageModal(true)}
            className="group relative"
            style={{
              opacity: scrollProgress,
              transform: `scale(${0.8 + (scrollProgress * 0.2)})`,
              transition: 'all 0.8s ease-out 0.2s'
            }}
          >
            {/* Outer Glow Ring */}
            <div className="absolute -inset-4 bg-[#D12A2C]/10 rounded-full blur-xl group-hover:bg-[#D12A2C]/20 transition-all duration-500" />

            {/* Pulse Rings */}
            <div className="absolute inset-0 rounded-full">
              {[...Array(3)].map((_, i) => (
                <div
                  key={i}
                  className="absolute inset-0 rounded-full border-2 border-[#D12A2C]/30 animate-ping"
                  style={{
                    animationDuration: '3s',
                    animationDelay: `${i * 1}s`
                  }}
                />
              ))}
            </div>

            {/* Main Button */}
            <div className="relative w-20 h-20 sm:w-24 sm:h-24 md:w-28 md:h-28 rounded-full bg-gradient-to-br from-[#D12A2C] to-[#8B1C1E] shadow-2xl flex items-center justify-center group-hover:shadow-[#D12A2C]/50 transition-all duration-300 group-hover:scale-105">
              <Play 
                className="w-8 h-8 sm:w-10 sm:h-10 md:w-12 md:h-12 text-white ml-1 group-hover:scale-110 transition-transform duration-300" 
                fill="white" 
              />
            </div>
          </button>

          {/* Language Icon Hint */}
          <div 
            className="mt-8 sm:mt-10 flex items-center gap-2 text-gray-500 text-xs sm:text-sm"
            style={{
              opacity: scrollProgress * 0.7,
              transition: 'opacity 0.8s ease-out 0.3s'
            }}
          >
            <Globe className="w-4 h-4" />
            <span className="font-['Inter',sans-serif]">√áoklu dil desteƒüi</span>
          </div>

          {/* Decorative Bottom Line */}
          <div 
            className="w-16 sm:w-20 h-0.5 bg-gradient-to-r from-transparent via-[#D12A2C] to-transparent mt-12 sm:mt-16"
            style={{
              opacity: scrollProgress,
              transform: `scaleX(${scrollProgress})`,
              transition: 'all 0.8s ease-out'
            }}
          />

        </div>

        {/* Bottom Indicator Dots */}
        <div
          className="absolute bottom-8 sm:bottom-12 left-1/2 -translate-x-1/2 flex gap-2 sm:gap-3"
          style={{ 
            opacity: scrollProgress * 0.6,
            transition: 'opacity 0.8s ease-out'
          }}
        >
          {[...Array(3)].map((_, i) => (
            <div
              key={i}
              className="w-1.5 h-1.5 sm:w-2 sm:h-2 rounded-full bg-[#D12A2C]/40"
              style={{
                animation: `pulse 2s ease-in-out infinite ${i * 0.3}s`
              }}
            />
          ))}
        </div>
      </section>

      {/* Language Selection Modal */}
      {showLanguageModal && (
        <div 
          className="fixed inset-0 z-[9999] flex items-center justify-center bg-black/60 backdrop-blur-sm px-4 animate-fadeIn"
          onClick={() => setShowLanguageModal(false)}
        >
          <div 
            className="relative bg-white rounded-2xl shadow-2xl w-full max-w-md overflow-hidden animate-scaleIn"
            onClick={(e) => e.stopPropagation()}
          >
            {/* Header */}
