import React, { useState } from 'react';
import { Share2, Facebook, Instagram, Linkedin, Twitter } from 'lucide-react';

const SocialMediaBox = () => {
  const [isHovered, setIsHovered] = useState(false);

  const socialLinks = [
    { 
      Icon: Facebook, 
      href: '#', 
      color: 'hover:bg-blue-600',
      label: 'Facebook' 
    },
    { 
      Icon: Instagram, 
      href: 'https://www.instagram.com/onderihl', 
      color: 'hover:bg-gradient-to-tr hover:from-purple-600 hover:via-pink-600 hover:to-orange-500',
      label: 'Instagram' 
    },
    { 
      Icon: X, 
      href: 'https://x.com/onderihl?ref_src=twsrc%5Egoogle%7Ctwcamp%5Eserp%7Ctwgr%5Eauthor', 
      color: 'hover:bg-sky-500',
      label: 'X' 
    },
  ];

  return (
    <div 
      className="fixed bottom-8 right-8 z-50 flex flex-col items-center gap-3"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Main Share Button */}
      <div className="relative group">
        <div className="w-14 h-14 rounded-full bg-gradient-to-br from-amber-700 to-amber-900 flex items-center justify-center cursor-pointer shadow-lg transition-all duration-300 hover:scale-110 hover:shadow-xl hover:from-amber-600 hover:to-amber-800">
          <Share2 size={24} color="#fff" />
        </div>

        {/* Pulse effect */}
        <div className="absolute inset-0 rounded-full bg-amber-700 animate-ping opacity-20"></div>
      </div>

      {/* Social Icons */}
      <div className={`flex flex-col gap-3 transition-all duration-500 ${
        isHovered ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4 pointer-events-none'
      }`}>
        {socialLinks.map(({ Icon, href, color, label }, index) => (
          <a
            key={label}
            href={href}
            aria-label={label}
            className={`w-12 h-12 rounded-full bg-amber-900 flex items-center justify-center cursor-pointer shadow-md transition-all duration-300 hover:scale-110 hover:shadow-lg ${color}`}
            style={{
              transitionDelay: isHovered ? `${index * 50}ms` : '0ms'
            }}
          >
            <Icon size={20} color="#fff" />
          </a>
        ))}
      </div>

      <style jsx>{`
        @keyframes ping {
          75%, 100% {
            transform: scale(1.5);
            opacity: 0;
          }
        }
      `}</style>
    </div>
  );
};

export default SocialMediaBox;
