// Gerekli kütüphaneler:
// npm install framer-motion lucide-react
import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
// Yeni ikonlarımızı buradan import ediyoruz
import { Home, LayoutGrid, Headphones, HelpCircle, Info } from 'lucide-react';

// Menü verisini ikonlarla güncelliyoruz
// 'label' alanı, erişilebilirlik (ekran okuyucular) için çok önemlidir.
const menuItems = [
  { id: 1, Icon: Home, href: '#', label: 'Anasayfa' },
  { id: 2, Icon: LayoutGrid, href: '#', label: 'Galeri' },
  { id: 3, Icon: Headphones, href: '#', label: 'Sesli Tur' },
  { id: 4, Icon: HelpCircle, href: '#', label: 'Bilgi Yarışması' },
  { id: 5, Icon: Info, href: '#', label: 'Hakkımızda' }
];

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [hoveredId, setHoveredId] = useState(null);

  // Scroll dinleyicisi
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Header'ın scroll durumuna göre animasyon varyantları
  const headerVariants = {
    top: {
      backgroundColor: 'rgba(255, 255, 255, 0)',
      paddingTop: '2rem',
      paddingBottom: '2rem',
    },
    scrolled: {
      backgroundColor: 'rgba(255, 251, 235, 0.95)', // amber-50
      paddingTop: '1rem',
      paddingBottom: '1rem',
      backdropFilter: 'blur(10px)',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.05)',
      borderBottom: '1px solid #fde68a' // amber-200
    },
  };

  return (
    <motion.nav
      className="w-full fixed top-0 left-0 z-[1000]"
      variants={headerVariants}
      animate={isScrolled ? 'scrolled' : 'top'}
      transition={{ duration: 0.3, ease: 'easeInOut' }}
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Tüm menüyü ortalamak için 'justify-center' kullanıyoruz.
          Artık logo veya mobil buton yok.
        */}
        <div className="flex justify-center items-center h-16">

          {/* Masaüstü Web Menüsü */}
          <div className="flex items-center space-x-2">
            <ul 
              className="flex items-center space-x-2 relative"
              onMouseLeave={() => setHoveredId(null)}
            >
              {menuItems.map((item) => (
                <li key={item.id} className="relative">
                  <a
                    href={item.href}
                    // p-3 ile kare/dairesel bir hover alanı sağlıyoruz
                    className="relative block p-3 text-lg font-semibold text-amber-900 transition-colors duration-300 hover:text-amber-700 z-10"
                    onMouseEnter={() => setHoveredId(item.id)}
                    aria-label={item.label} // Erişilebilirlik için
                  >
                    {/* "Magic Ink" Efekti */}
                    {hoveredId === item.id && (
                      <motion.div
                        layoutId="magic-ink" 
                        className="absolute inset-0 bg-amber-800 rounded-lg z-0"
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        transition={{ type: 'spring', stiffness: 300, damping: 25 }}
                      />
                    )}

                    {/* İkonun kendisi */}
                    <span className={`relative z-10 transition-colors ${hoveredId === item.id ? 'text-white' : ''}`}>
                      <item.Icon size={24} />
                    </span>
                  </a>
                </li>
              ))}
            </ul>
          </div>

        </div>
      </div>

      {/* Mobil menü ve hamburger buton tamamen kaldırıldı. */}

    </motion.nav>
  );
};

export default Header;
