import React, { useRef, useState, useEffect } from 'react';
import { Menu } from 'lucide-react';
import { IoHomeOutline } from 'react-icons/io5';
import { HiOutlinePhotograph } from 'react-icons/hi';
import { BsMicFill } from 'react-icons/bs';
import { IoTrophyOutline } from 'react-icons/io5';
import { AiOutlineInfoCircle } from 'react-icons/ai';

const Header = () => {
  const [bgStyle, setBgStyle] = useState({ left: '0px', width: '0px' });
  const [isScrolled, setIsScrolled] = useState(false);
  const [menuOpen, setMenuOpen] = useState(true);
  const menuRef = useRef(null);

  const menuItems = [
    { id: 1, text: 'Anasayfa', icon: IoHomeOutline },
    { id: 2, text: 'Galeri', icon: HiOutlinePhotograph },
    { id: 3, text: 'Sesli Tur', icon: BsMicFill },
    { id: 4, text: 'Bilgi Yarışması', icon: IoTrophyOutline },
    { id: 5, text: 'Hakkımızda', icon: AiOutlineInfoCircle }
  ];

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      if (scrollPosition > 50) {
        setIsScrolled(true);
        setMenuOpen(false);
      } else {
        setIsScrolled(false);
        setMenuOpen(true);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleHover = (e) => {
    if (!menuRef.current) return;

    const item = e.currentTarget;
    const rect = item.getBoundingClientRect();
    const menuRect = menuRef.current.getBoundingClientRect();

    const width = rect.width;
    const left = rect.left - menuRect.left;

    setBgStyle({
      left: `${left}px`,
      width: `${width}px`
    });
  };

  const handleMouseLeave = () => {
    setBgStyle({ left: '0px', width: '0px' });
  };

  return (
    <nav className={`w-full fixed top-0 left-0 z-[1000] transition-all duration-300 ease-in-out ${isScrolled ? '' : 'mt-[2%]'}`}>
      <div className="max-w-[1400px] mx-auto px-4">
        <div className="flex justify-center items-center h-16">

          {/* Menu Container */}
          <div className="relative h-full w-full max-w-[800px]">
            {isScrolled && (
              <div 
                className="flex items-center justify-center cursor-pointer text-amber-800 z-[3] p-2 rounded transition-all duration-300"
                onClick={() => setMenuOpen(!menuOpen)}
              >
                <div className="p-2.5 rounded shadow-[0_5px_15px_rgba(0,0,0,0.15)] bg-amber-50 border border-amber-200">
                  <Menu size={24} />
                </div>
              </div>
            )}

            <div 
              ref={menuRef}
              className={`relative flex justify-around items-center rounded-[10px] bg-gradient-to-r from-amber-50 to-stone-50 border border-red-200/30 z-[1] overflow-hidden transition-all duration-500 ${
                menuOpen ? 'opacity-100 translate-y-0 h-16' : 'opacity-0 -translate-y-5 h-0'
              }`}
              onMouseLeave={handleMouseLeave}
            >
              {menuItems.map((item) => {
                const Icon = item.icon;
                return (
                  <a
                    key={item.id}
                    href="#"
                    className="relative flex flex-col items-center justify-center gap-1 font-['Maven_Pro',sans-serif] font-semibold text-[clamp(10px,0.9vw,12px)] no-underline px-[clamp(12px,1.5vw,20px)] py-[clamp(8px,0.8vw,12px)] rounded-[9px] transition-colors duration-300 z-[2] whitespace-nowrap text-amber-900 hover:text-white group"
                    onMouseEnter={handleHover}
                  >
                    <Icon className="text-[clamp(20px,2vw,28px)] transition-transform duration-300 group-hover:scale-110" />
                  </a>
                );
              })}

              <div 
                className="absolute top-1/2 -translate-y-1/2 h-[50px] rounded-[9px] transition-all duration-300 z-0 shadow-lg"
                style={{
                  ...bgStyle,
                  backgroundColor: '#451a03'
                }}
              ></div>
            </div>
          </div>

        </div>
      </div>
    </nav>
  );
};

export default Header;
