import React, { useState, useEffect } from 'react';
import { Upload, X, PlusCircle, ChevronLeft, ChevronRight, Send } from 'lucide-react';
import Header from '../../components/Header/HeaderController'; // Header'ınızın olduğunu varsayıyorum

// Örnek veriler
const initialGalleryItems = [
  {
    id: 1,
    image: 'https://pbs.twimg.com/media/Gd4zOf6WgAsWgMs?format=jpg&name=small',
    message: 'Sebilin muhteşem detayları beni çok etkiledi. Osmanlı mimarisinin inceliği burada çok net görülüyor.',
  },
  {
    id: 2,
    image: 'https://pbs.twimg.com/media/GdiyWK_XYAA_y49?format=jpg&name=small',
    message: 'Cami içi görülmeye değer!',
  },
  {
    id: 3,
    image: 'https://pbs.twimg.com/media/GdepXjiXEAATeY8?format=jpg&name=small',
    message: 'Medrese avlusunda tarihi hissettim. 280 yıllık bu yapı hala ayakta duruyor.',
  },
  {
    id: 4,
    image: 'https://pbs.twimg.com/media/GdI3sl-W8AAKNYy?format=jpg&name=small',
    message: 'Barok süslemeler harika.',
  },
  {
    id: 5,
    image: 'https://pbs.twimg.com/media/GcafYF4WMAApNxy?format=jpg&name=small',
    message: 'Ailecek geldik, herkese tavsiye ederim.',
  },
  {
    id: 6,
    image: 'https://pbs.twimg.com/media/GcA0bvfXEAAIcr1?format=jpg&name=small',
    message: 'Çeşmenin detayları muhteşem!',
  },
];

// Stil (CSS) - Kaydırma çubuğunu güzelleştirmek için
const customScrollbarStyles = `
  .horizontal-scrollbar {
    -ms-overflow-style: none; /* IE and Edge */
    scrollbar-width: thin; /* Firefox */
    scrollbar-color: #D12A2C #f3eade; /* (thumb color) (track color) */
  }

  .horizontal-scrollbar::-webkit-scrollbar {
    height: 8px;
  }

  .horizontal-scrollbar::-webkit-scrollbar-track {
    background: #D12A2C;
    border-radius: 10px;
  }

  .horizontal-scrollbar::-webkit-scrollbar-thumb {
    background-color: #D12A2C; /* Koyu kehribar/kahve */
    border-radius: 10px;
    border: 2px solid #f3eade;
  }
`;

// GÖRSEL YÜKLEME MODALI
const UploadModal = ({ setShowModal, setGalleryItems }) => {
  const [message, setMessage] = useState('');
  const [previewImage, setPreviewImage] = useState(null);
  const [imageFile, setImageFile] = useState(null);

  const handleImageSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewImage(reader.result);
        setImageFile(file);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (previewImage && message && imageFile) {
      const newItem = {
        id: Date.now(), // Benzersiz ID için
        image: previewImage, // Gerçekte burası backend'den gelen URL olacak
        message: message,
      };
      // Yeni öğeyi listenin başına ekle
      setGalleryItems((prevItems) => [newItem, ...prevItems]);
      setShowModal(false);
    }
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/70 backdrop-blur-sm animate-fade-in">
      <div className="bg-white rounded-2xl shadow-xl w-full max-w-lg p-6 m-4 relative transform transition-all animate-slide-up">
        <button
          onClick={() => setShowModal(false)}
          className="absolute top-4 right-4 text-gray-500 hover:text-gray-900 transition-colors"
        >
          <X size={24} />
        </button>
        <h2 className="text-2xl font-bold text-red-900 mb-6 text-center">
          Külliye Hatıranızı Paylaşın
        </h2>

        <form onSubmit={handleSubmit}>
          {/* Görsel Yükleme Alanı */}
          <div
            className={`w-full h-48 rounded-lg border-2 border-dashed ${
              previewImage ? 'border-red-700' : 'border-red-300'
            } flex items-center justify-center text-center p-4 transition-all`}
          >
            {previewImage ? (
              <div className="relative w-full h-full">
                <img
                  src={previewImage}
                  alt="Önizleme"
                  className="w-full h-full object-contain rounded-md"
                />
                <button
                  type="button"
                  onClick={() => {
                    setPreviewImage(null);
                    setImageFile(null);
                  }}
                  className="absolute top-1 right-1 bg-white/70 rounded-full p-1"
                >
                  <X size={16} className="text-red-600" />
                </button>
              </div>
            ) : (
              <label className="flex flex-col items-center justify-center cursor-pointer text-gray-500">
                <Upload size={32} />
                <span className="mt-2 text-sm font-medium">Görsel seçin</span>
                <input
                  type="file"
                  className="hidden"
                  accept="image/*"
                  onChange={handleImageSelect}
                />
              </label>
            )}
          </div>

          {/* Mesaj Alanı */}
          <textarea
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Bu kare hakkında ne düşünüyorsunuz?"
            className="w-full mt-4 p-3 h-28 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-red-500 focus:border-transparent"
            required
          />

          {/* Gönder Butonu */}
          <button
            type="submit"
            disabled={!previewImage || !message}
            className="w-full mt-6 bg-red-800 text-white rounded-lg py-3 font-bold flex items-center justify-center gap-2 hover:bg-red-700 transition-colors disabled:bg-gray-400 disabled:cursor-not-allowed"
          >
            <Send size={18} />
            <span>Paylaş</span>
          </button>
        </form>
      </div>
    </div>
  );
};


// ANA GALERİ SAYFASI
const UserGalleryPage = () => {
  const [galleryItems, setGalleryItems] = useState(initialGalleryItems);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showUploadModal, setShowUploadModal] = useState(false);

  // galleryItems güncellendiğinde (yeni item eklendiğinde) ilk sıraya geç
  useEffect(() => {
    if (galleryItems.length > initialGalleryItems.length) {
      setCurrentIndex(0);
    }
  }, [galleryItems]);

  const currentItem = galleryItems[currentIndex];

  const nextItem = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % galleryItems.length);
  };

  const prevItem = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + galleryItems.length) % galleryItems.length);
  };

  const selectItem = (index) => {
    setCurrentIndex(index);
  };

  return (
    <>
      <style>{customScrollbarStyles}</style>

      {/* CSS Animasyonları için */}
      <style>{`
        @keyframes fade-in {
          from { opacity: 0; }
          to { opacity: 1; }
        }
        @keyframes slide-up {
          from { transform: translateY(20px); opacity: 0; }
          to { transform: translateY(0); opacity: 1; }
        }
        .animate-fade-in { animation: fade-in 0.3s ease-out; }
        .animate-slide-up { animation: slide-up 0.4s ease-out; }
      `}</style>

      <div className="w-full min-h-screen bg-gradient-to-b from-red-50 via-red-50 to-red-100 py-16 sm:py-20 px-4">

        <Header />

        <div className="max-w-7xl mx-auto flex flex-col h-full">

          {/* Başlık */}
          <div className="text-center my-8 sm:my-12">
            <h1 className="text-4xl sm:text-5xl font-bold text-red-900 mb-4 tracking-tight">
              Ziyaretçi Gözünden
            </h1>
            <p className="text-lg text-red-800/80 font-serif italic">
              "Külliye'de zamanın durduğu o anlar..."
            </p>
          </div>

          {/* 1. ANA SAHNE (SHOWCASE) */}
          <div className="flex-grow flex justify-center mb-8">
            {galleryItems.length > 0 && currentItem ? (
              <div className="w-full max-w-4xl relative animate-fade-in">
                {/* Ana Görsel */}
                <div className="bg-white rounded-2xl shadow-xl overflow-hidden border border-red-200/50">
                  <div className="aspect-w-16 aspect-h-10">
                    <img
                      src={currentItem.image}
                      alt={`Galeri ${currentItem.id}`}
                      className="w-full h-full object-cover"
                      key={currentItem.id}
                    />
                  </div>
                </div>

                {/* Mesaj */}
                <div className="mt-6 text-center">
                  <p className="text-lg sm:text-xl font-serif italic text-red-900/90 leading-relaxed max-w-2xl mx-auto">
                    “{currentItem.message}”
                  </p>
                </div>

                {/* Navigasyon Okları (Büyük Ekran) */}
                <button 
                  onClick={prevItem} 
                  className="absolute left-0 top-1/2 -translate-y-1/2 -translate-x-12 hidden lg:flex w-12 h-12 bg-white/50 rounded-full items-center justify-center text-red-800 hover:bg-white/80 hover:scale-105 transition-all"
                >
                  <ChevronLeft size={28} />
                </button>
                <button 
                  onClick={nextItem} 
                  className="absolute right-0 top-1/2 -translate-y-1/2 translate-x-12 hidden lg:flex w-12 h-12 bg-white/50 rounded-full items-center justify-center text-red-800 hover:bg-white/80 hover:scale-105 transition-all"
                >
                  <ChevronRight size={28} />
                </button>
              </div>
            ) : (
              <div className="text-center p-10 text-red-700">
                Henüz paylaşılan bir hatıra yok. İlk paylaşan siz olun!
              </div>
            )}
          </div>

          {/* 2. ANı ŞERİDİ (YATAY SCROLL) */}
          <div className="w-full mt-auto py-6">
            <div className="flex overflow-x-auto gap-4 sm:gap-6 pb-4 horizontal-scrollbar">

              {/* YÜKLEME KARTI (HER ZAMAN İLK) */}
              <div 
                onClick={() => setShowUploadModal(true)}
                className="flex-shrink-0 w-32 h-44 sm:w-40 sm:h-52 rounded-xl bg-red-100 border-2 border-dashed border-red-600 flex flex-col items-center justify-center text-center p-4 cursor-pointer hover:bg-red-200 hover:border-red-700 transition-all transform hover:scale-105"
              >
                <PlusCircle className="w-10 h-10 text-red-700 mb-2" />
                <span className="font-bold text-red-900 text-sm sm:text-base">Anını Paylaş</span>
                <span className="text-xs text-red-800/70 mt-1">Görselinizi yükleyin</span>
              </div>

              {/* GALERİ ÖĞELERİ */}
              {galleryItems.map((item, index) => (
  <div
    key={item.id}
    onClick={() => selectItem(index)}
    className={`flex-shrink-0 w-32 h-44 sm:w-40 sm:h-52 rounded-xl cursor-pointer shadow-md hover:shadow-xl transition-all duration-300 transform hover:scale-105 ${
      index === currentIndex ? 'ring-4 ring-offset-2 ring-red-700' : 'ring-2 ring-white/50'
    }`}
  >
    <img
      src={item.image}
      alt={`Thumbnail ${item.id}`}
      className="w-full h-full object-cover rounded-xl"
    />
  </div>
))}
            </div>

            {/* Navigasyon Okları (Mobil) */}
            {galleryItems.length > 0 && (
              <div className="flex lg:hidden justify-center gap-8 mt-6">
                <button 
                  onClick={prevItem} 
                  className="w-12 h-12 bg-white/80 rounded-full flex items-center justify-center text-red-800 shadow-md"
                >
                  <ChevronLeft size={28} />
                </button>
                <button 
                  onClick={nextItem} 
                  className="w-12 h-12 bg-white/80 rounded-full flex items-center justify-center text-red-800 shadow-md"
                >
                  <ChevronRight size={28} />
                </button>
              </div>
            )}

          </div>
        </div>
      </div>

      {/* Upload Modalı */}
      {showUploadModal && (
        <UploadModal 
          setShowModal={setShowUploadModal} 
          setGalleryItems={setGalleryItems} 
        />
      )}
    </>
  );
};

export default UserGalleryPage;
