import React, { useState } from 'react';
import { getAdminId } from '../../utils/adminAuth';
import { adminAPI } from '../../services/api';

const RezervasyonDetayModal = ({ rezervasyon, onClose, onUpdate }) => {
    const [actionLoading, setActionLoading] = useState(false);
    const [redNedeni, setRedNedeni] = useState('');
    const [showRedForm, setShowRedForm] = useState(false);

    if (!rezervasyon) return null;

    const handleOnayla = async () => {
        if (!window.confirm('Bu rezervasyonu onaylamak istediğinize emin misiniz?')) {
            return;
        }

        setActionLoading(true);
        try {
            const adminId = getAdminId();
            await adminAPI.onaylaRezervasyon(rezervasyon.id, adminId);
            alert('Rezervasyon başarıyla onaylandı!');
            onUpdate();
            onClose();
        } catch (error) {
            alert(error.message || 'Rezervasyon onaylanırken hata oluştu');
        } finally {
            setActionLoading(false);
        }
    };

    const handleReddet = async () => {
        if (!redNedeni.trim()) {
            alert('Lütfen red nedeni giriniz');
            return;
        }

        if (!window.confirm('Bu rezervasyonu reddetmek istediğinize emin misiniz?')) {
            return;
        }

        setActionLoading(true);
        try {
            const adminId = getAdminId();
            await adminAPI.reddetRezervasyon(rezervasyon.id, adminId, redNedeni);
            alert('Rezervasyon reddedildi');
            onUpdate();
            onClose();
        } catch (error) {
            alert(error.message || 'Rezervasyon reddedilirken hata oluştu');
        } finally {
            setActionLoading(false);
        }
    };

    const handleIptal = async () => {
        if (!window.confirm('Bu rezervasyonu iptal etmek istediğinize emin misiniz?')) {
            return;
        }

        setActionLoading(true);
        try {
            const adminId = getAdminId();
            await adminAPI.iptalRezervasyon(rezervasyon.id, adminId);
            alert('Rezervasyon iptal edildi');
            onUpdate();
            onClose();
        } catch (error) {
            alert(error.message || 'Rezervasyon iptal edilirken hata oluştu');
        } finally {
            setActionLoading(false);
        }
    };

    const getDurumBadge = (durum) => {
        const badges = {
            beklemede: 'bg-yellow-100 text-yellow-800 border-yellow-200',
            onaylandi: 'bg-green-100 text-green-800 border-green-200',
            reddedildi: 'bg-red-100 text-red-800 border-red-200',
            iptal: 'bg-gray-100 text-gray-800 border-gray-200'
        };

        const labels = {
            beklemede: 'Beklemede',
            onaylandi: 'Onaylandı',
            reddedildi: 'Reddedildi',
            iptal: 'İptal'
        };

        return (
            <span className={`px-3 py-1 rounded-full text-sm font-medium border ${badges[durum]}`}>
                {labels[durum]}
            </span>
        );
    };

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-xl shadow-2xl max-w-4xl w-full max-h-[90vh] overflow-y-auto">
                {/* Header */}
                <div className="sticky top-0 bg-white border-b border-gray-200 px-6 py-4 flex items-center justify-between">
                    <div>
                        <h2 className="text-xl font-bold text-gray-900">
                            Rezervasyon Detayları
                        </h2>
                        <p className="text-sm text-gray-600 mt-1">
                            ID: #{rezervasyon.id}
                        </p>
                    </div>
                    <button
                        onClick={onClose}
                        className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
                    >
                        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                        </svg>
                    </button>
                </div>

                {/* Content */}
                <div className="p-6 space-y-6">
                    {/* Durum */}
                    <div className="flex items-center justify-between p-4 bg-gray-50 rounded-lg">
                        <span className="text-sm font-medium text-gray-700">Rezervasyon Durumu</span>
                        {getDurumBadge(rezervasyon.durum)}
                    </div>

                    {/* Kurum Bilgileri */}
                    <div>
                        <h3 className="text-lg font-semibold text-gray-900 mb-3">Kurum Bilgileri</h3>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            <div>
                                <label className="text-sm text-gray-600">Yetkili Adı Soyadı</label>
                                <p className="font-medium text-gray-900">{rezervasyon.yetkili_ad_soyad}</p>
                            </div>
                            <div>
                                <label className="text-sm text-gray-600">Kurum Adı</label>
                                <p className="font-medium text-gray-900">{rezervasyon.kurum_adi}</p>
                            </div>
                            <div>
                                <label className="text-sm text-gray-600">İl / İlçe</label>
                                <p className="font-medium text-gray-900">{rezervasyon.il} / {rezervasyon.ilce}</p>
                            </div>
                            <div>
                                <label className="text-sm text-gray-600">Telefon</label>
                                <p className="font-medium text-gray-900">{rezervasyon.telefon}</p>
                            </div>
                        </div>
                    </div>

                    {/* Rezervasyon Bilgileri */}
                    <div>
                        <h3 className="text-lg font-semibold text-gray-900 mb-3">Rezervasyon Bilgileri</h3>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            <div>
                                <label className="text-sm text-gray-600">Tarih</label>
                                <p className="font-medium text-gray-900">
                                    {new Date(rezervasyon.tarih).toLocaleDateString('tr-TR', {
                                        day: 'numeric',
                                        month: 'long',
                                        year: 'numeric'
                                    })}
                                </p>
                            </div>
                            <div>
                                <label className="text-sm text-gray-600">Saat Dilimi</label>
                                <p className="font-medium text-gray-900">{rezervasyon.saat_dilimi}</p>
                            </div>
                            <div>
                                <label className="text-sm text-gray-600">Toplam Kişi Sayısı</label>
                                <p className="font-medium text-gray-900">{rezervasyon.toplam_kisi} kişi</p>
                            </div>
                            <div>
                                <label className="text-sm text-gray-600">İkramlar</label>
                                <div className="flex gap-2 mt-1">
                                    {rezervasyon.kahvalti && (
                                        <span className="px-2 py-1 bg-emerald-100 text-emerald-700 text-xs rounded">Kahvaltı</span>
                                    )}
                                    {rezervasyon.ogle_yemegi && (
                                        <span className="px-2 py-1 bg-blue-100 text-blue-700 text-xs rounded">Öğle Yemeği</span>
                                    )}
                                    {rezervasyon.waffle && (
                                        <span className="px-2 py-1 bg-purple-100 text-purple-700 text-xs rounded">Waffle</span>
                                    )}
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Mekanlar */}
                    <div>
                        <h3 className="text-lg font-semibold text-gray-900 mb-3">Mekanlar</h3>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                            {Object.entries(rezervasyon.mekanlar).map(([mekan, kisi]) => (
                                <div key={mekan} className="p-3 bg-gray-50 rounded-lg flex items-center justify-between">
                                    <span className="font-medium text-gray-900">{mekan}</span>
                                    <span className="text-emerald-600 font-semibold">{kisi} kişi</span>
                                </div>
                            ))}
                        </div>
                    </div>

                    {/* Açıklama */}
                    {rezervasyon.aciklama && (
                        <div>
                            <h3 className="text-lg font-semibold text-gray-900 mb-3">Açıklama</h3>
                            <p className="text-gray-700 bg-gray-50 p-4 rounded-lg">{rezervasyon.aciklama}</p>
                        </div>
                    )}

                    {/* Öğrenciler */}
                    {rezervasyon.ogrenciler && rezervasyon.ogrenciler.length > 0 && (
                        <div>
                            <h3 className="text-lg font-semibold text-gray-900 mb-3">
                                Öğrenci Listesi ({rezervasyon.ogrenciler.length} kişi)
                            </h3>
                            <div className="max-h-60 overflow-y-auto border border-gray-200 rounded-lg">
                                <table className="w-full">
                                    <thead className="bg-gray-50 sticky top-0">
                                        <tr>
                                            <th className="px-4 py-2 text-left text-xs font-medium text-gray-600">#</th>
                                            <th className="px-4 py-2 text-left text-xs font-medium text-gray-600">Ad Soyad</th>
                                            <th className="px-4 py-2 text-left text-xs font-medium text-gray-600">Sınıf</th>
                                            <th className="px-4 py-2 text-left text-xs font-medium text-gray-600">Cinsiyet</th>
                                            <th className="px-4 py-2 text-left text-xs font-medium text-gray-600">Telefon</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {rezervasyon.ogrenciler.map((ogrenci, index) => (
                                            <tr key={ogrenci.id} className="border-t border-gray-100">
                                                <td className="px-4 py-2 text-sm text-gray-900">{index + 1}</td>
                                                <td className="px-4 py-2 text-sm text-gray-900">{ogrenci.ad_soyad}</td>
                                                <td className="px-4 py-2 text-sm text-gray-600">{ogrenci.sinif}</td>
                                                <td className="px-4 py-2 text-sm text-gray-600">
                                                    {ogrenci.cinsiyet === 'erkek' ? 'Erkek' : 'Kız'}
                                                </td>
                                                <td className="px-4 py-2 text-sm text-gray-600">{ogrenci.telefon || '-'}</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    )}

                    {/* Red Nedeni (varsa) */}
                    {rezervasyon.red_nedeni && (
                        <div className="p-4 bg-red-50 border border-red-200 rounded-lg">
                            <h4 className="font-semibold text-red-900 mb-2">Red Nedeni:</h4>
                            <p className="text-red-700">{rezervasyon.red_nedeni}</p>
                        </div>
                    )}

                    {/* Admin ve Tarih Bilgileri */}
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-gray-600">
                        <div>
                            <span className="font-medium">Oluşturulma: </span>
                            {new Date(rezervasyon.olusturma_tarihi).toLocaleString('tr-TR')}
                        </div>
                        {rezervasyon.onay_tarihi && (
                            <div>
                                <span className="font-medium">İşlem Tarihi: </span>
                                {new Date(rezervasyon.onay_tarihi).toLocaleString('tr-TR')}
                            </div>
                        )}
                        {rezervasyon.onaylayan_admin && (
                            <div>
                                <span className="font-medium">İşlem Yapan: </span>
                                {rezervasyon.onaylayan_admin}
                            </div>
                        )}
                    </div>
                </div>

                {/* Actions */}
                {rezervasyon.durum === 'beklemede' && (
                    <div className="border-t border-gray-200 px-6 py-4 bg-gray-50">
                        {!showRedForm ? (
                            <div className="flex flex-wrap gap-3 justify-end">
                                <button
                                    onClick={() => setShowRedForm(true)}
                                    disabled={actionLoading}
                                    className="px-6 py-2 border border-red-300 text-red-700 rounded-lg hover:bg-red-50 transition-colors disabled:opacity-50"
                                >
                                    Reddet
                                </button>
                                <button
                                    onClick={handleOnayla}
                                    disabled={actionLoading}
                                    className="px-6 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 transition-colors disabled:opacity-50 flex items-center gap-2"
                                >
                                    {actionLoading ? (
                                        <>
                                            <svg className="animate-spin h-5 w-5" viewBox="0 0 24 24">
                                                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" />
                                                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z" />
                                            </svg>
                                            İşleniyor...
                                        </>
                                    ) : (
                                        'Onayla'
                                    )}
                                </button>
                            </div>
                        ) : (
                            <div className="space-y-3">
                                <textarea
                                    value={redNedeni}
                                    onChange={(e) => setRedNedeni(e.target.value)}
                                    placeholder="Red nedenini yazınız..."
                                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-red-500 focus:border-transparent"
                                    rows="3"
                                />
                                <div className="flex gap-3 justify-end">
                                    <button
                                        onClick={() => {
                                            setShowRedForm(false);
                                            setRedNedeni('');
                                        }}
                                        className="px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50"
                                    >
                                        Vazgeç
                                    </button>
                                    <button
                                        onClick={handleReddet}
                                        disabled={actionLoading || !redNedeni.trim()}
                                        className="px-6 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors disabled:opacity-50"
                                    >
                                        Reddet ve Kaydet
                                    </button>
                                </div>
                            </div>
                        )}
                    </div>
                )}

                {rezervasyon.durum === 'onaylandi' && (
                    <div className="border-t border-gray-200 px-6 py-4 bg-gray-50 flex justify-end">
                        <button
                            onClick={handleIptal}
                            disabled={actionLoading}
                            className="px-6 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors disabled:opacity-50"
                        >
                            İptal Et
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default RezervasyonDetayModal;
