import React, { useState } from 'react';
import { Upload, X, Image as ImageIcon } from 'lucide-react';

const UserGalleryPage = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [message, setMessage] = useState('');
  const [previewImage, setPreviewImage] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Örnek veriler (backend gelince burası API'den gelecek)
  const [galleryItems, setGalleryItems] = useState([
    {
      id: 1,
      image: 'https://pbs.twimg.com/media/Gd4zOf6WgAsWgMs?format=jpg&name=small',
      message: 'Sebilin muhteşem detayları beni çok etkiledi. Osmanlı mimarisinin inceliği burada çok net görülüyor.',
    },
    {
      id: 2,
      image: 'https://pbs.twimg.com/media/GdiyWK_XYAA_y49?format=jpg&name=small',
      message: 'Cami içi görülmeye değer!',
    },
    {
      id: 3,
      image: 'https://pbs.twimg.com/media/GdepXjiXEAATeY8?format=jpg&name=small',
      message: 'Medrese avlusunda tarihi hissettim. 280 yıllık bu yapı hala ayakta duruyor.',
    },
    {
      id: 4,
      image: 'https://pbs.twimg.com/media/GdI3sl-W8AAKNYy?format=jpg&name=small',
      message: 'Barok süslemeler harika.',
    },
    {
      id: 5,
      image: 'https://pbs.twimg.com/media/GcafYF4WMAApNxy?format=jpg&name=small',
      message: 'Ailecek geldik, herkese tavsiye ederim.',
    },
    {
      id: 6,
      image: 'https://pbs.twimg.com/media/GcA0bvfXEAAIcr1?format=jpg&name=small',
      message: 'Çeşmenin detayları muhteşem!',
    },
  ]);

  const handleImageSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewImage(reader.result);
        setSelectedImage(file);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Backend gelince burada API'ye gönderilecek
    if (previewImage && message) {
      const newItem = {
        id: galleryItems.length + 1,
        image: previewImage,
        message: message,
      };
      setGalleryItems([newItem, ...galleryItems]);

      // Reset form
      setPreviewImage(null);
      setSelectedImage(null);
      setMessage('');
    }
  };

  const openModal = (item) => {
    setShowModal(item);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <div className="w-full min-h-screen bg-gradient-to-b from-amber-50 to-stone-50 py-20 px-4">

      {/* Header Section */}
      <div className="max-w-7xl mx-auto mb-16 text-center">
        <h1 className="text-4xl sm:text-5xl md:text-6xl font-bold text-gray-900 mb-4 font-['Maven_Pro',sans-serif]">
          Anılarınızı Paylaşın
        </h1>
        <p className="text-lg sm:text-xl text-gray-600 max-w-2xl mx-auto">
          Beşirağa Külliyesi'nden çektiğiniz fotoğrafları ve deneyimlerinizi bizimle paylaşın
        </p>
      </div>

      {/* Upload Section */}
      <div className="max-w-3xl mx-auto mb-20">
        <form onSubmit={handleSubmit} className="bg-white rounded-2xl shadow-lg p-6 sm:p-8 border border-amber-200/30">

          {/* Image Upload */}
          <div className="mb-6">
            <label className="block text-sm font-semibold text-gray-700 mb-3">
              Fotoğraf Yükle
            </label>

            {!previewImage ? (
              <label className="flex flex-col items-center justify-center w-full h-64 border-2 border-dashed border-amber-300 rounded-xl cursor-pointer bg-amber-50/50 hover:bg-amber-100/50 transition-all duration-300">
                <div className="flex flex-col items-center justify-center pt-5 pb-6">
                  <Upload className="w-12 h-12 text-amber-700 mb-3" />
                  <p className="mb-2 text-sm text-gray-600">
                    <span className="font-semibold">Tıklayın</span> veya sürükleyip bırakın
                  </p>
                  <p className="text-xs text-gray-500">PNG, JPG (MAX. 10MB)</p>
                </div>
                <input
                  type="file"
                  className="hidden"
                  accept="image/*"
                  onChange={handleImageSelect}
                />
              </label>
            ) : (
              <div className="relative w-full h-64 rounded-xl overflow-hidden">
                <img
                  src={previewImage}
                  alt="Preview"
                  className="w-full h-full object-cover"
                />
                <button
                  type="button"
                  onClick={() => {
                    setPreviewImage(null);
                    setSelectedImage(null);
                  }}
                  className="absolute top-3 right-3 w-8 h-8 bg-red-500 rounded-full flex items-center justify-center text-white hover:bg-red-600 transition-colors"
                >
                  <X size={20} />
                </button>
              </div>
            )}
          </div>

          {/* Message Input */}
          <div className="mb-6">
            <label className="block text-sm font-semibold text-gray-700 mb-3">
              Mesajınız
            </label>
            <textarea
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="Deneyiminizi bizimle paylaşın..."
              rows="4"
              className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-amber-500 focus:border-transparent resize-none"
              required
            />
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            disabled={!previewImage || !message}
            className="w-full bg-amber-900 text-white py-3 rounded-xl font-semibold hover:bg-amber-800 transition-colors disabled:bg-gray-300 disabled:cursor-not-allowed"
          >
            Paylaş
          </button>
        </form>
      </div>

      {/* Gallery Section - Masonry Grid */}
      <div className="max-w-7xl mx-auto">
        <h2 className="text-3xl sm:text-4xl font-bold text-gray-900 mb-8 text-center">
          Ziyaretçilerimizden Kareler
        </h2>

        {/* Masonry Grid */}
        <div className="columns-1 sm:columns-2 lg:columns-3 xl:columns-4 gap-4 space-y-4">
          {galleryItems.map((item) => (
            <div
              key={item.id}
              className="break-inside-avoid mb-4 group cursor-pointer"
              onClick={() => openModal(item)}
            >
              <div className="bg-white rounded-xl overflow-hidden shadow-md hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1">
                <div className="relative overflow-hidden">
                  <img
                    src={item.image}
                    alt="User submission"
                    className="w-full h-auto object-cover group-hover:scale-110 transition-transform duration-300"
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                </div>
                <div className="p-4">
                  <p className="text-gray-700 text-sm leading-relaxed line-clamp-3">
                    {item.message}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Modal for Full Image */}
      {showModal && (
        <div
          className="fixed inset-0 z-50 flex items-center justify-center bg-black/80 p-4"
          onClick={closeModal}
        >
          <div
            className="relative max-w-4xl w-full bg-white rounded-2xl overflow-hidden"
            onClick={(e) => e.stopPropagation()}
          >
            <button
              onClick={closeModal}
              className="absolute top-4 right-4 w-10 h-10 bg-white rounded-full flex items-center justify-center text-gray-800 hover:bg-gray-100 transition-colors z-10"
            >
              <X size={24} />
            </button>
            <img
              src={showModal.image}
              alt="Full view"
              className="w-full h-auto max-h-[70vh] object-contain"
            />
            <div className="p-6">
              <p className="text-gray-700 text-base leading-relaxed">
                {showModal.message}
              </p>
            </div>
          </div>
        </div>
      )}

    </div>
  );
};

export default UserGalleryPage;
