import React, { useState, useEffect } from 'react';
import { Upload, X, PlusCircle, ChevronLeft, ChevronRight, Send, Camera } from 'lucide-react';
import Header from '../../components/Header/HeaderController'; 

// Örnek veriler
const initialGalleryItems = [
  {
    id: 1,
    image: 'https://pbs.twimg.com/media/Gd4zOf6WgAsWgMs?format=jpg&name=small',
    message: 'Sebilin muhteşem detayları beni çok etkiledi. Osmanlı mimarisinin inceliği burada çok net görülüyor.',
    author: 'Ahmet Y.'
  },
  {
    id: 2,
    image: 'https://pbs.twimg.com/media/GdiyWK_XYAA_y49?format=jpg&name=small',
    message: 'Cami içi görülmeye değer! Huzur dolu bir atmosfer.',
    author: 'Zeynep K.'
  },
  {
    id: 3,
    image: 'https://pbs.twimg.com/media/GdepXjiXEAATeY8?format=jpg&name=small',
    message: 'Medrese avlusunda tarihi hissettim. 280 yıllık bu yapı hala ayakta duruyor.',
    author: 'Murat S.'
  },
  {
    id: 4,
    image: 'https://pbs.twimg.com/media/GdI3sl-W8AAKNYy?format=jpg&name=small',
    message: 'Barok süslemeler harika, kesinlikle detaylı incelenmeli.',
    author: 'Elif B.'
  },
  {
    id: 5,
    image: 'https://pbs.twimg.com/media/GcafYF4WMAApNxy?format=jpg&name=small',
    message: 'Ailecek geldik, herkese tavsiye ederim.',
    author: 'Caner T.'
  },
  {
    id: 6,
    image: 'https://pbs.twimg.com/media/GcA0bvfXEAAIcr1?format=jpg&name=small',
    message: 'Çeşmenin detayları muhteşem!',
    author: 'Selin A.'
  },
];

// GÖRSEL YÜKLEME MODALI (DARK THEME)
const UploadModal = ({ setShowModal, setGalleryItems }) => {
  const [message, setMessage] = useState('');
  const [previewImage, setPreviewImage] = useState(null);
  const [imageFile, setImageFile] = useState(null);

  const handleImageSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewImage(reader.result);
        setImageFile(file);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (previewImage && message && imageFile) {
      const newItem = {
        id: Date.now(),
        image: previewImage,
        message: message,
        author: 'Siz'
      };
      setGalleryItems((prevItems) => [newItem, ...prevItems]);
      setShowModal(false);
    }
  };

  return (
    <div className="fixed inset-0 z-[60] flex items-center justify-center bg-black/80 backdrop-blur-md animate-fade-in p-4">
      <div className="bg-slate-900 border border-white/10 rounded-3xl shadow-2xl w-full max-w-lg p-8 relative transform transition-all animate-slide-up">
        <button
          onClick={() => setShowModal(false)}
          className="absolute top-4 right-4 text-slate-400 hover:text-white transition-colors bg-white/5 p-2 rounded-full hover:bg-white/10"
        >
          <X size={20} />
        </button>

        <div className="text-center mb-8">
            <h2 className="text-3xl font-serif text-white mb-2">
            Anı Ölümsüzleştir
            </h2>
            <p className="text-slate-400 text-sm">Külliye ziyaretinizden bir kare paylaşın</p>
        </div>

        <form onSubmit={handleSubmit}>
          {/* Görsel Yükleme Alanı */}
          <div
            className={`w-full h-52 rounded-2xl border-2 border-dashed ${
              previewImage ? 'border-amber-500/50 bg-slate-950' : 'border-white/10 bg-white/5 hover:border-amber-500/30 hover:bg-white/10'
            } flex items-center justify-center text-center p-2 transition-all group overflow-hidden relative`}
          >
            {previewImage ? (
              <div className="relative w-full h-full group-hover:opacity-100">
                <img
                  src={previewImage}
                  alt="Önizleme"
                  className="w-full h-full object-contain rounded-xl"
                />
                <button
                  type="button"
                  onClick={() => {
                    setPreviewImage(null);
                    setImageFile(null);
                  }}
                  className="absolute top-2 right-2 bg-slate-900/80 hover:bg-red-500/80 text-white rounded-full p-2 transition-colors backdrop-blur-sm"
                >
                  <X size={16} />
                </button>
              </div>
            ) : (
              <label className="flex flex-col items-center justify-center cursor-pointer w-full h-full text-slate-400 group-hover:text-amber-400 transition-colors">
                <div className="p-4 rounded-full bg-white/5 mb-3 group-hover:scale-110 transition-transform">
                     <Camera size={32} />
                </div>
                <span className="text-sm font-medium">Fotoğraf Seçmek İçin Tıkla</span>
                <input
                  type="file"
                  className="hidden"
                  accept="image/*"
                  onChange={handleImageSelect}
                />
              </label>
            )}
          </div>

          {/* Mesaj Alanı */}
          <div className="mt-6">
            <textarea
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Bu karenin hikayesi nedir? (Düşüncelerinizi yazın...)"
                className="w-full p-4 h-32 bg-slate-950 border border-white/10 rounded-xl text-slate-200 text-sm focus:outline-none focus:ring-2 focus:ring-amber-500/50 focus:border-amber-500/50 placeholder:text-slate-600 resize-none"
                required
            />
          </div>

          {/* Gönder Butonu */}
          <button
            type="submit"
            disabled={!previewImage || !message}
            className="w-full mt-8 bg-amber-600 text-white rounded-xl py-4 font-bold flex items-center justify-center gap-3 hover:bg-amber-500 transition-all disabled:bg-slate-800 disabled:text-slate-500 disabled:cursor-not-allowed shadow-[0_0_20px_rgba(245,158,11,0.2)] hover:shadow-[0_0_30px_rgba(245,158,11,0.4)]"
          >
            <Send size={18} />
            <span>Paylaş</span>
          </button>
        </form>
      </div>
    </div>
  );
};


// ANA GALERİ SAYFASI
const UserGalleryPage = () => {
  const [galleryItems, setGalleryItems] = useState(initialGalleryItems);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showUploadModal, setShowUploadModal] = useState(false);

  useEffect(() => {
    if (galleryItems.length > initialGalleryItems.length) {
      setCurrentIndex(0);
    }
  }, [galleryItems]);

  const currentItem = galleryItems[currentIndex];

  const nextItem = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % galleryItems.length);
  };

  const prevItem = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + galleryItems.length) % galleryItems.length);
  };

  const selectItem = (index) => {
    setCurrentIndex(index);
  };

  return (
    <div className="min-h-screen bg-slate-950 text-slate-200 font-sans selection:bg-amber-500/30 relative overflow-hidden flex flex-col">

      {/* --- BACKGROUND LAYERS (Diğer sayfalarla aynı) --- */}
      <div className="fixed inset-0 z-0 pointer-events-none">
        {/* 1. Görsel (Resim yolunu '/images/foto.jpeg' olarak varsaydım) */}
        <img 
          src="/images/foto.jpeg"
          alt="Beşirağa Külliyesi Arka Plan"
          className="w-full h-full object-cover opacity-20 animate-slow-zoom" 
        />

        {/* 2. Koyu Degrade Overlay */}
        <div className="absolute inset-0 bg-gradient-to-b from-slate-950 via-slate-950/80 to-slate-950" />

        {/* 3. Desen Overlay */}
        <div 
          className="absolute inset-0 opacity-5 mix-blend-overlay"
          style={{
            backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23fbbf24' fill-opacity='0.4'%3E%3Cpath d='M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`
          }} 
        />
      </div>

      {/* --- CONTENT --- */}
      <div className="relative z-10 flex flex-col min-h-screen">
        <Header />

        <div className="container mx-auto px-4 pt-32 pb-8 flex-grow flex flex-col">

          {/* Başlık */}
          <div className="text-center mb-12 animate-fade-in-up">
            <h1 className="text-4xl md:text-6xl font-serif text-white mb-4 tracking-tight">
              Ziyaretçi <span className="text-transparent bg-clip-text bg-gradient-to-r from-amber-200 to-amber-500">Gözünden</span>
            </h1>
            <p className="text-lg text-slate-400 font-light max-w-2xl mx-auto">
              Taşların fısıldadığı hikayeleri, sizin objektifinizden dinliyoruz.
            </p>
          </div>

          {/* 1. ANA SAHNE (SHOWCASE) */}
          <div className="flex-grow flex flex-col items-center justify-center mb-10 w-full">
            {galleryItems.length > 0 && currentItem ? (
              <div className="w-full max-w-5xl relative animate-fade-in group">

                <div className="grid md:grid-cols-5 gap-8 bg-slate-900/40 backdrop-blur-xl border border-white/10 rounded-3xl p-6 shadow-2xl">

                    {/* Görsel Kısmı */}
                    <div className="md:col-span-3 relative rounded-2xl overflow-hidden aspect-[4/3] md:aspect-auto shadow-lg">
                        <img
                        src={currentItem.image}
                        alt={`Galeri ${currentItem.id}`}
                        className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-105"
                        key={currentItem.id}
                        />
                        <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-transparent opacity-60"></div>
                    </div>

                    {/* Metin Kısmı */}
                    <div className="md:col-span-2 flex flex-col justify-center text-left py-4 pr-4">
                        <div className="mb-6">
                           <div className="text-amber-500 text-6xl font-serif leading-none opacity-50">"</div>
                           <p className="text-xl md:text-2xl font-serif text-slate-200 italic leading-relaxed -mt-4 pl-4 relative z-10">
                            {currentItem.message}
                           </p>
                        </div>

                        <div className="mt-auto flex items-center gap-3 pl-4">
                            <div className="w-10 h-10 rounded-full bg-gradient-to-br from-amber-400 to-amber-700 flex items-center justify-center text-white font-bold">
                                {currentItem.author ? currentItem.author.charAt(0) : 'Z'}
                            </div>
                            <div>
                                <div className="text-white font-medium">{currentItem.author || 'Ziyaretçi'}</div>
                                <div className="text-xs text-slate-500 uppercase tracking-wider">Ziyaretçi</div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Navigasyon Okları (Desktop) */}
                <button 
                  onClick={prevItem} 
                  className="absolute -left-6 top-1/2 -translate-y-1/2 hidden lg:flex w-14 h-14 bg-white/5 border border-white/10 backdrop-blur-md rounded-full items-center justify-center text-slate-300 hover:bg-amber-500 hover:text-white hover:scale-110 transition-all shadow-xl z-20"
                >
                  <ChevronLeft size={32} />
                </button>
                <button 
                  onClick={nextItem} 
                  className="absolute -right-6 top-1/2 -translate-y-1/2 hidden lg:flex w-14 h-14 bg-white/5 border border-white/10 backdrop-blur-md rounded-full items-center justify-center text-slate-300 hover:bg-amber-500 hover:text-white hover:scale-110 transition-all shadow-xl z-20"
                >
                  <ChevronRight size={32} />
                </button>
              </div>
            ) : (
              <div className="text-center p-20 text-slate-500 bg-white/5 rounded-3xl border border-white/5">
                Henüz paylaşılan bir hatıra yok. İlk paylaşan siz olun!
              </div>
            )}
          </div>

          {/* 2. ANI ŞERİDİ (YATAY SCROLL) */}
          <div className="w-full mt-auto py-8 border-t border-white/5 bg-slate-900/30 backdrop-blur-sm">
            <div className="container mx-auto">
                <div className="flex overflow-x-auto gap-4 px-4 pb-4 horizontal-scrollbar items-center">

                {/* YÜKLEME KARTI */}
                <div 
                    onClick={() => setShowUploadModal(true)}
                    className="flex-shrink-0 w-36 h-24 rounded-xl border border-dashed border-amber-500/30 bg-amber-500/5 hover:bg-amber-500/10 hover:border-amber-500 flex flex-col items-center justify-center text-center cursor-pointer transition-all group"
                >
                    <PlusCircle className="w-8 h-8 text-amber-500 mb-1 group-hover:scale-110 transition-transform" />
                    <span className="text-xs font-bold text-amber-400 uppercase tracking-wide">Paylaş</span>
                </div>

                {/* GALERİ ÖĞELERİ */}
                {galleryItems.map((item, index) => (
                    <div
                    key={item.id}
                    onClick={() => selectItem(index)}
                    className={`flex-shrink-0 w-36 h-24 rounded-xl overflow-hidden cursor-pointer relative group transition-all duration-300 ${
                        currentIndex === index 
                        ? 'ring-2 ring-amber-500 ring-offset-2 ring-offset-slate-900 scale-105 opacity-100' 
                        : 'opacity-50 hover:opacity-100 hover:scale-105'
                    }`}
                    >
                    <img
                        src={item.image}
                        alt={`Thumbnail ${item.id}`}
                        className="w-full h-full object-cover"
                    />
                    {/* Aktif İse Overlay Kalkar, Değilse Koyuluk Kalır */}
                    {currentIndex !== index && <div className="absolute inset-0 bg-black/40 group-hover:bg-transparent transition-colors" />}
                    </div>
                ))}
                </div>
            </div>

            {/* Navigasyon Okları (Mobil) */}
            {galleryItems.length > 0 && (
              <div className="flex lg:hidden justify-center gap-6 mt-4">
                <button 
                  onClick={prevItem} 
                  className="w-10 h-10 bg-white/10 border border-white/10 rounded-full flex items-center justify-center text-white shadow-md active:bg-amber-500"
                >
                  <ChevronLeft size={20} />
                </button>
                <button 
                  onClick={nextItem} 
                  className="w-10 h-10 bg-white/10 border border-white/10 rounded-full flex items-center justify-center text-white shadow-md active:bg-amber-500"
                >
                  <ChevronRight size={20} />
                </button>
              </div>
            )}

          </div>
        </div>
      </div>

      {/* Upload Modalı */}
      {showUploadModal && (
        <UploadModal 
          setShowModal={setShowUploadModal} 
          setGalleryItems={setGalleryItems} 
        />
      )}

      {/* STYLES */}
      <style jsx>{`
        @import url('https://fonts.googleapis.com/css2?family=Playfair+Display:ital,wght@0,400;0,600;1,400&family=Inter:wght@300;400;500;600&display=swap');

        .font-serif { font-family: 'Playfair Display', serif; }
        .font-sans { font-family: 'Inter', sans-serif; }

        /* Arka Plan Zoom Animasyonu */
        @keyframes slow-zoom {
          0% { transform: scale(1); }
          100% { transform: scale(1.1); }
        }
        .animate-slow-zoom {
          animation: slow-zoom 20s linear infinite alternate;
        }

        @keyframes fade-in {
          from { opacity: 0; }
          to { opacity: 1; }
        }
        .animate-fade-in {
          animation: fade-in 0.3s ease-out;
        }

        @keyframes fadeInUp {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-fade-in-up {
          animation: fadeInUp 0.6s ease-out forwards;
        }

        @keyframes slideUp {
            from { opacity: 0; transform: translateY(20px); }
            to { opacity: 1; transform: translateY(0); }
        }
        .animate-slide-up {
            animation: slideUp 0.4s ease-out forwards;
        }

        /* Custom Scrollbar for Dark Theme */
        .horizontal-scrollbar {
          -ms-overflow-style: none;
          scrollbar-width: thin;
          scrollbar-color: #475569 #0f172a; /* Thumb Track */
        }
        .horizontal-scrollbar::-webkit-scrollbar {
          height: 6px;
        }
        .horizontal-scrollbar::-webkit-scrollbar-track {
          background: #0f172a; /* Slate 900 */
          border-radius: 10px;
        }
        .horizontal-scrollbar::-webkit-scrollbar-thumb {
          background-color: #475569; /* Slate 600 */
          border-radius: 10px;
        }
        .horizontal-scrollbar::-webkit-scrollbar-thumb:hover {
            background-color: #f59e0b; /* Amber 500 */
        }
      `}</style>
    </div>
  );
};

export default UserGalleryPage;
