import React, { useEffect, useState, useRef } from 'react';
import { Play, X, Globe, Volume2 } from 'lucide-react';

const PodcastTransition = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [scrollProgress, setScrollProgress] = useState(0);
  const [showLanguageModal, setShowLanguageModal] = useState(false);
  const sectionRef = useRef(null);

  const languages = [
    { code: 'tr', name: 'T√ºrk√ße', flag: 'üáπüá∑' },
    { code: 'en', name: 'English', flag: 'üá¨üáß' },
    { code: 'ar', name: 'ÿßŸÑÿπÿ±ÿ®Ÿäÿ©', flag: 'üá∏üá¶' }
  ];

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        setIsVisible(entry.isIntersecting);
      },
      { 
        threshold: [0, 0.5, 1],
        rootMargin: "-10% 0px -10% 0px"
      }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => observer.disconnect();
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      if (!sectionRef.current) return;

      const rect = sectionRef.current.getBoundingClientRect();
      const viewportHeight = window.innerHeight;
      const elementMiddle = rect.top + rect.height / 2;
      const viewportMiddle = viewportHeight / 2;
      const distance = Math.abs(elementMiddle - viewportMiddle);
      const maxDistance = (viewportHeight + rect.height) / 2;

      let progress = 1 - (distance / maxDistance);
      progress = Math.min(Math.max(progress, 0), 1);

      setScrollProgress(progress);
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll();
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleLanguageSelect = (langCode) => {
    console.log(`Selected language: ${langCode}`);
    // Burada podcast √ßalma fonksiyonunu ekleyebilirsiniz
    setShowLanguageModal(false);
  };

  return (
    <>
      <section 
        ref={sectionRef} 
        className="w-full min-h-screen flex items-center justify-center relative overflow-hidden"
      >
        {/* Background Image */}
        <div className="absolute inset-0 w-full h-full">
          <img
            src="/path-to-your-horizontal-image.jpg"
            alt="Be≈üiraƒüa K√ºlliyesi"
            className="w-full h-full object-cover"
            style={{
              transform: `scale(${1 + scrollProgress * 0.1})`,
              transition: 'transform 0.6s ease-out'
            }}
          />

          {/* Gradient Overlay - Daha koyu ve profesyonel */}
          <div 
            className="absolute inset-0 bg-gradient-to-b from-red-900/85 via-red-900/75 to-red-900/90"
            style={{
              opacity: 0.7 + (scrollProgress * 0.2)
            }}
          />

          {/* Subtle texture overlay */}
          <div 
            className="absolute inset-0 opacity-[0.03]"
            style={{
              backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23ffffff' fill-opacity='1'%3E%3Cpath d='M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`
            }}
          />
        </div>

        {/* Main Content Container */}
        <div className="relative z-10 flex flex-col items-center justify-center px-4 sm:px-6 lg:px-8 max-w-5xl mx-auto text-center">

          {/* Icon Badge */}
          <div 
            className="mb-6 sm:mb-8"
            style={{
              opacity: scrollProgress,
              transform: `translateY(${(1 - scrollProgress) * 20}px)`,
              transition: 'all 0.8s ease-out'
            }}
          >
            <div className="inline-flex items-center gap-2 px-4 py-2 rounded-full bg-white/10 backdrop-blur-md border border-white/20">
              <Volume2 className="w-4 h-4 text-[#D12A2C]" />
              <span className="text-xs sm:text-sm font-medium text-white/90 font-['Inter',sans-serif]">
                Sesli Tur Deneyimi
              </span>
            </div>
          </div>

          {/* Title */}
          <h2 
            className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-bold text-white mb-4 sm:mb-6 font-['Maven_Pro',sans-serif] tracking-tight"
            style={{
              opacity: scrollProgress,
              transform: `translateY(${(1 - scrollProgress) * 30}px)`,
              transition: 'all 0.8s ease-out 0.1s',
              textShadow: '0 4px 12px rgba(0, 0, 0, 0.5)'
            }}
          >
