import React, { useState } from 'react';
import { Menu, X } from 'lucide-react';

const MobileHeader = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const menuItems = [
    { id: 1, text: 'Anasayfa' },
    { id: 2, text: 'Ziyaretçi Galerisi' },
    { id: 3, text: 'Sesli Keşif' },
    { id: 4, text: 'Beşir Ağa Testi' },
    { id: 5, text: 'Hakkımızda' }
  ];


  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
    document.body.style.overflow = isMenuOpen ? 'auto' : 'hidden';
  };

  return (
    <nav className="fixed top-5 left-0 w-full z-[1000]  ">
      <div className="px-4">
        <div className="flex justify-center items-center h-16">

          {/* Menu Toggle Button */}
          <button 
            className="shadow-[0_2px_10px_rgba(120,53,15,0.1)] border-b border-amber-200/30 border-none p-2 cursor-pointer text-amber-800 flex items-center justify-center rounded transition-all duration-300 z-[1001] hover:bg-amber-100 rounded shadow-[0_5px_15px_rgba(0,0,0,0.15)] bg-amber-50 border border-amber-200"
            onClick={toggleMenu}
          >
            {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>

        {/* Mobile Menu */}
        <div 
          className={`fixed top-0 left-0 w-full h-screen bg-gradient-to-br from-amber-50 via-stone-50 to-amber-100 transition-transform duration-300 ${
            isMenuOpen ? 'translate-y-0 visible' : '-translate-y-full invisible'
          }`}
        >
          {/* Menu Items */}
          <div className="flex flex-col items-center justify-center gap-6 pt-20 px-8 pb-8 h-full">
            {menuItems.map((item) => (
              <a 
                key={item.id} 
                href="#" 
                className="font-['Maven_Pro',sans-serif] text-xl font-semibold text-amber-900 no-underline py-2.5 px-5 rounded-lg transition-all duration-300 w-full text-center hover:bg-amber-200/30 hover:text-amber-950"
                onClick={toggleMenu}
              >
                {item.text}
              </a>
            ))}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default MobileHeader;
