/**
 * Dosya: frontend/src/pages/RezervasyonPage/index.jsx
 * Açıklama: Çok adımlı rezervasyon formu ana sayfası
 */

import React, { useState } from 'react';
import Header from '../../components/Header/HeaderController';
import Step1KurumBilgileri from '../../components/Rezervasyon/Step1KurumBilgileri';
import Step2TarihSecimi from '../../components/Rezervasyon/Step2TarihSecimi';
import Step3SaatSecimi from '../../components/Rezervasyon/Step3SaatSecimi';
import Step4MekanSecimi from '../../components/Rezervasyon/Step4MekanSecimi';
import Step5Aciklama from '../../components/Rezervasyon/Step5Aciklama';
import Step6OgrenciBilgileri from '../../components/Rezervasyon/Step6OgrenciBilgileri';

const RezervasyonPage = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    // Adım 1: Kurum Bilgileri
    yetkili_ad_soyad: '',
    kurum_adi: '',
    kurum_tipi: 'okul',
    il: '',
    ilce: '',
    telefon: '',

    // Adım 2: Tarih
    tarih: '',

    // Adım 3: Saat
    saat_dilimi: '',

    // Adım 4: Mekanlar
    mekanlar: {},

    // Adım 5: Açıklama
    aciklama: '',

    // Adım 6: Öğrenciler
    ogrenciler: []
  });

  const totalSteps = 6;

  const nextStep = () => {
    if (currentStep < totalSteps) {
      setCurrentStep(currentStep + 1);
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  const updateFormData = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <Step1KurumBilgileri
            formData={formData}
            updateFormData={updateFormData}
            nextStep={nextStep}
          />
        );
      case 2:
        return (
          <Step2TarihSecimi
            formData={formData}
            updateFormData={updateFormData}
            nextStep={nextStep}
            prevStep={prevStep}
          />
        );
      case 3:
        return (
          <Step3SaatSecimi
            formData={formData}
            updateFormData={updateFormData}
            nextStep={nextStep}
            prevStep={prevStep}
          />
        );
      case 4:
        return (
          <Step4MekanSecimi
            formData={formData}
            updateFormData={updateFormData}
            nextStep={nextStep}
            prevStep={prevStep}
          />
        );
      case 5:
        return (
          <Step5Aciklama
            formData={formData}
            updateFormData={updateFormData}
            nextStep={nextStep}
            prevStep={prevStep}
          />
        );
      case 6:
        return (
          <Step6OgrenciBilgileri
            formData={formData}
            updateFormData={updateFormData}
            prevStep={prevStep}
          />
        );
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-stone-50 via-amber-50 to-stone-50">
      <Header />

      {/* Hero Section */}
      <section className="bg-gradient-to-r from-stone-900 via-amber-950 to-stone-900 py-16">
        <div className="max-w-7xl mx-auto px-4">
          <div className="text-center text-white">
            <h1 className="text-4xl md:text-5xl font-bold mb-4">
              Rezervasyon Oluştur
            </h1>
            <p className="text-amber-200 text-lg">
              Beşirağa Medresesi'ni ziyaret etmek için rezervasyon yapın
            </p>
          </div>
        </div>
      </section>

      {/* Progress Bar */}
      <div className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 py-6">
          <div className="flex items-center justify-between">
            {[1, 2, 3, 4, 5, 6].map((step) => (
              <React.Fragment key={step}>
                <div className="flex flex-col items-center">
                  <div
                    className={`w-10 h-10 rounded-full flex items-center justify-center font-bold transition-all duration-300 ${
                      step === currentStep
                        ? 'bg-amber-500 text-white scale-110'
                        : step < currentStep
                        ? 'bg-green-500 text-white'
                        : 'bg-gray-200 text-gray-500'
                    }`}
                  >
                    {step < currentStep ? '✓' : step}
                  </div>
                  <span className="text-xs mt-2 text-gray-600 hidden md:block">
                    {step === 1 && 'Kurum'}
                    {step === 2 && 'Tarih'}
                    {step === 3 && 'Saat'}
                    {step === 4 && 'Mekan'}
                    {step === 5 && 'Açıklama'}
                    {step === 6 && 'Öğrenciler'}
                  </span>
                </div>
                {step < 6 && (
                  <div
                    className={`flex-1 h-1 mx-2 transition-all duration-300 ${
                      step < currentStep ? 'bg-green-500' : 'bg-gray-200'
                    }`}
                  />
                )}
              </React.Fragment>
            ))}
          </div>
        </div>
      </div>

      {/* Form Content */}
      <div className="max-w-4xl mx-auto px-4 py-12">
        <div className="bg-white rounded-lg shadow-lg p-8">
          {renderStep()}
        </div>
      </div>
    </div>
  );
};

export default RezervasyonPage;
