import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { adminAPI } from '../../services/api';
import StatCard from '../../components/admin/StatCard';

const AdminDashboard = () => {
    const navigate = useNavigate();
    const [stats, setStats] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchDashboardData();
    }, []);

    const fetchDashboardData = async () => {
        try {
            setLoading(true);
            const response = await adminAPI.getDashboardStats();

            if (response.success) {
                setStats(response.data);
            }
        } catch (err) {
            setError(err.message || 'İstatistikler yüklenirken hata oluştu');
        } finally {
            setLoading(false);
        }
    };

    // İstatistik kartları için iconlar
    const icons = {
        beklemede: (
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
        ),
        onaylandi: (
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
        ),
        reddedildi: (
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
        ),
        ziyaretci: (
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
            </svg>
        ),
        bugun: (
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
            </svg>
        ),
        buAy: (
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
            </svg>
        )
    };

    if (error) {
        return (
            <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg">
                {error}
            </div>
        );
    }

    return (
        <div className="space-y-6">
            {/* Başlık */}
            <div>
                <h1 className="text-2xl md:text-3xl font-bold text-gray-900">
                    Dashboard
                </h1>
                <p className="text-gray-600 mt-1">
                    Rezervasyon sistemi genel bakış
                </p>
            </div>

            {/* İstatistik Kartları */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                <StatCard
                    title="Bekleyen Rezervasyonlar"
                    value={stats?.durum_sayilari?.beklemede}
                    icon={icons.beklemede}
                    color="yellow"
                    loading={loading}
                />
                <StatCard
                    title="Onaylanan Rezervasyonlar"
                    value={stats?.durum_sayilari?.onaylandi}
                    icon={icons.onaylandi}
                    color="emerald"
                    loading={loading}
                />
                <StatCard
                    title="Reddedilen Rezervasyonlar"
                    value={stats?.durum_sayilari?.reddedildi}
                    icon={icons.reddedildi}
                    color="red"
                    loading={loading}
                />
                <StatCard
                    title="Toplam Ziyaretçi"
                    value={stats?.toplam_ziyaretci}
                    icon={icons.ziyaretci}
                    color="blue"
                    loading={loading}
                />
                <StatCard
                    title="Bugünkü Rezervasyonlar"
                    value={stats?.bugun}
                    icon={icons.bugun}
                    color="purple"
                    loading={loading}
                />
                <StatCard
                    title="Bu Ay Toplam"
                    value={stats?.bu_ay}
                    icon={icons.buAy}
                    color="teal"
                    loading={loading}
                />
            </div>

            {/* Son 7 Gün Tablosu */}
            {!loading && stats?.son_yedi_gun && stats.son_yedi_gun.length > 0 && (
                <div className="bg-white rounded-xl shadow-sm p-6 border border-gray-100">
                    <h2 className="text-lg font-semibold text-gray-900 mb-4">
                        Son 7 Gün Rezervasyon Trendi
                    </h2>
                    <div className="overflow-x-auto">
                        <table className="w-full">
                            <thead>
                                <tr className="border-b border-gray-200">
                                    <th className="text-left py-3 px-4 text-sm font-medium text-gray-600">
                                        Tarih
                                    </th>
                                    <th className="text-center py-3 px-4 text-sm font-medium text-gray-600">
                                        Rezervasyon Sayısı
                                    </th>
                                    <th className="text-right py-3 px-4 text-sm font-medium text-gray-600">
                                        Toplam Kişi
                                    </th>
                                </tr>
                            </thead>
                            <tbody>
                                {stats.son_yedi_gun.map((item, index) => (
                                    <tr key={index} className="border-b border-gray-100 hover:bg-gray-50">
                                        <td className="py-3 px-4 text-sm text-gray-900">
                                            {new Date(item.tarih).toLocaleDateString('tr-TR', {
                                                day: 'numeric',
                                                month: 'long',
                                                year: 'numeric'
                                            })}
                                        </td>
                                        <td className="py-3 px-4 text-sm text-center">
                                            <span className="inline-flex items-center px-3 py-1 rounded-full bg-emerald-100 text-emerald-700 font-medium">
                                                {item.sayi}
                                            </span>
                                        </td>
                                        <td className="py-3 px-4 text-sm text-right text-gray-900 font-medium">
                                            {item.toplam_kisi} kişi
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminDashboard;
