import React, { useRef, useEffect } from 'react';

const Portfolio = () => {
  const scrollRef = useRef(null);

  const portfolioItems = [
    { id: 1, image: 'https://pbs.twimg.com/media/Gd4zOf6WgAsWgMs?format=jpg&name=small', title: 'Sebil', category: 'Mimari Fotoğraf' },
    { id: 2, image: 'https://pbs.twimg.com/media/GdiyWK_XYAA_y49?format=jpg&name=small', title: 'Cami İç Mekan', category: 'Mimari Detay' },
    { id: 3, image: 'https://pbs.twimg.com/media/GdepXjiXEAATeY8?format=jpg&name=small', title: 'Medrese Avlusu', category: 'Tarihi Mekan' },
    { id: 4, image: 'https://pbs.twimg.com/media/GdI3sl-W8AAKNYy?format=jpg&name=small', title: 'Kütüphane', category: 'Barok Süsleme' },
    { id: 5, image: 'https://pbs.twimg.com/media/GcafYF4WMAApNxy?format=jpg&name=small', title: 'Tekke', category: 'Tarihi Yapı' },
    { id: 6, image: 'https://pbs.twimg.com/media/GcA0bvfXEAAIcr1?format=jpg&name=small', title: 'Çeşme', category: 'Osmanlı Sanatı' },
  ];

  // Sonsuz scroll için items'ları çoğaltıyoruz
  const duplicatedItems = [...portfolioItems, ...portfolioItems];

  useEffect(() => {
    const scrollContainer = scrollRef.current;
    let animationFrameId;
    let isHovered = false;

    const scroll = () => {
      if (scrollContainer && !isHovered) {
        scrollContainer.scrollLeft += 1; // Scroll hızı

        // Scroll sonuna geldiğinde başa dön
        if (scrollContainer.scrollLeft >= scrollContainer.scrollWidth / 2) {
          scrollContainer.scrollLeft = 0;
        }
      }
      animationFrameId = requestAnimationFrame(scroll);
    };

    scroll();

    const handleMouseEnter = () => {
      isHovered = true;
    };

    const handleMouseLeave = () => {
      isHovered = false;
    };

    scrollContainer.addEventListener('mouseenter', handleMouseEnter);
    scrollContainer.addEventListener('mouseleave', handleMouseLeave);

    return () => {
      cancelAnimationFrame(animationFrameId);
      if (scrollContainer) {
        scrollContainer.removeEventListener('mouseenter', handleMouseEnter);
        scrollContainer.removeEventListener('mouseleave', handleMouseLeave);
      }
    };
  }, []);

  return (
    <div className="w-full overflow-hidden">
      <div 
        ref={scrollRef}
        className="flex select-none p-8 gap-0.5 overflow-x-hidden scroll-smooth active:cursor-grabbing"
      >
        {duplicatedItems.map((item, index) => (
          <div 
            key={`${item.id}-${index}`} 
            className="flex-shrink-0 w-[400px] h-[400px] relative overflow-hidden group"
          >
            <div className="w-full h-full relative overflow-hidden">
              <img 
                src={item.image} 
                alt={item.title} 
                className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-110" 
              />
              <div className="absolute top-0 left-0 w-full h-full bg-gradient-to-t from-amber-950/90 via-amber-900/70 to-transparent flex flex-col justify-center items-center opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                <h3 className="text-white text-2xl font-['Maven_Pro',sans-serif] font-semibold m-0 mb-2">
                  {item.title}
                </h3>
                <p className="text-amber-100 text-base m-0 opacity-90">
                  {item.category}
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Portfolio;
