// Gerekli kütüphaneler:
// npm install framer-motion lucide-react
import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
// Sadece bu ikonlara ihtiyacımız var
import { Home, LayoutGrid, Headphones, HelpCircle, Info } from 'lucide-react';

// Artık TEK BİR menü listemiz var, hem mobil hem masaüstü için.
const menuItems = [
  { id: 1, Icon: Home, href: '#', label: 'Anasayfa' },
  { id: 2, Icon: LayoutGrid, href: '#', label: 'Galeri' },
  { id: 3, Icon: Headphones, href: '#', label: 'Sesli Tur' },
  { id: 4, Icon: HelpCircle, href: '#', label: 'Bilgi Yarışması' },
  { id: 5, Icon: Info, href: '#', label: 'Hakkımızda' }
];

// Masaüstü Header'ı için scroll animasyonu
const desktopHeaderVariants = {
  top: {
    backgroundColor: 'rgba(255, 255, 255, 0)',
    paddingTop: '2rem',
    paddingBottom: '2rem',
  },
  scrolled: {
    backgroundColor: 'rgba(255, 255, 255, 0)',
    paddingTop: '1rem',
    paddingBottom: '1rem',
  },
};

// --- Ana Bileşen ---

const Header = () => {
  // Masaüstü state'leri
  const [isScrolled, setIsScrolled] = useState(false);
  const [hoveredId, setHoveredId] = useState(null);

  // Mobil 'isMenuOpen' state'i ve ilgili tüm kodlar kaldırıldı.

  // Scroll dinleyicisi (Hala Sadece Masaüstü için)
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    const mediaQuery = window.matchMedia('(min-width: 768px)');

    if (mediaQuery.matches) {
      window.addEventListener('scroll', handleScroll);
    }

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <>
      {/* =================================
        1. MASAÜSTÜ HEADER (md ve üzeri)
        =================================
      */}


      {/* =================================
        2. MOBİL ALT MENÜ (md'den küçük)
        =================================
      */}
      <nav 
        className="
          fixed top-0 left-0 w-full h-16 z-[1000] md:hidden 
          bg-amber-50/95 backdrop-blur-md 
          border-t border-amber-200 
          shadow-[0_-5px_15px_rgba(0,0,0,0.05)]"
      >
        {/* max-w-md ile çok geniş mobil ekranlarda (phablet) ikonların yayılmasını engelleriz */}
        <div className="max-w-md mx-auto h-full">
          <ul className="flex justify-around items-center h-full">
            {menuItems.map((item) => (
              <li key={item.id}>
                <a
                  href={item.href}
                  aria-label={item.label}
                  className="flex flex-col items-center justify-center p-3 text-amber-900 rounded-lg transition-colors duration-200 hover:bg-amber-100"
                >
                  <item.Icon size={24} />
                </a>
              </li>
            ))}
          </ul>
        </div>
      </nav>

      {/* Açılan menü ve hamburger buton tamamen kaldırıldı. */}
    </>
  );
};

export default Header;
