// --- MODAL (Görsel Yükleme) ---
const UploadModal = ({ setShowModal, setGalleryItems }) => {
  const [message, setMessage] = useState('');
  const [previewImage, setPreviewImage] = useState(null);

  // Dosya seçme
  const handleImageSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (previewImage && message) {
      const newItem = {
        id: Date.now(),
        image: previewImage,
        message: message,
      };
      setGalleryItems((prevItems) => [newItem, ...prevItems]);
      setShowModal(false);
    }
  };

  return (
    <div className="fixed inset-0 z-[60] flex items-center justify-center bg-black/90 backdrop-blur-sm animate-fade-in p-4">
      <div className="bg-slate-900 border border-white/10 rounded-2xl w-full max-w-lg overflow-hidden shadow-2xl animate-scale-up">
        <div className="p-4 border-b border-white/5 flex justify-between items-center bg-white/5">
          <span className="text-white font-medium">Yeni Anı Ekle</span>
          <button onClick={() => setShowModal(false)} className="text-slate-400 hover:text-white transition-colors">
            <X size={20} />
          </button>
        </div>

        <div className="p-6">
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className={`relative w-full aspect-video rounded-xl border-2 border-dashed transition-all overflow-hidden group ${
              previewImage ? 'border-amber-500/50' : 'border-white/10 hover:border-white/30'
            }`}>
              {previewImage ? (
                <>
                  <img src={previewImage} alt="Önizleme" className="w-full h-full object-cover" />
                  <button
                    type="button"
                    onClick={() => { setPreviewImage(null); }}
                    className="absolute top-2 right-2 bg-black/60 text-white rounded-full p-2 hover:bg-red-600 transition-colors"
                  >
                    <X size={16} />
                  </button>
                </>
              ) : (
                <label className="flex flex-col items-center justify-center w-full h-full cursor-pointer text-slate-400 hover:text-white hover:bg-white/5 transition-all">
                  <Camera size={32} className="mb-2 opacity-50" />
                  <span className="text-sm">Fotoğraf Seçin</span>
                  <input type="file" className="hidden" accept="image/*" onChange={handleImageSelect} />
                </label>
              )}
            </div>

            <div>
              <textarea
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  placeholder="Bu fotoğrafın hikayesi..."
                  className="w-full p-4 bg-black/20 border border-white/10 rounded-xl text-white text-sm focus:outline-none focus:border-amber-500/50 resize-none h-24 placeholder:text-slate-600"
              />
            </div>

            <button
              type="submit"
              disabled={!previewImage || !message}
              className="w-full py-3 bg-white text-black font-medium rounded-lg hover:bg-amber-400 transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2"
            >
              <Send size={16} />
              <span>Paylaş</span>
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};


// --- ANA SAYFA ---
const UserGalleryPage = () => {
  const [galleryItems, setGalleryItems] = useState(initialGalleryItems);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [isFullScreen, setIsFullScreen] = useState(false); // Tam ekran state'i

  useEffect(() => {
    if (galleryItems.length > initialGalleryItems.length) setCurrentIndex(0);
  }, [galleryItems]);

  const currentItem = galleryItems[currentIndex];

  const nextItem = useCallback((e) => {
    if(e) e.stopPropagation();
    setCurrentIndex((prev) => (prev + 1) % galleryItems.length);
  }, [galleryItems.length]);

  const prevItem = useCallback((e) => {
    if(e) e.stopPropagation();
    setCurrentIndex((prev) => (prev - 1 + galleryItems.length) % galleryItems.length);
  }, [galleryItems.length]);

  // Klavye Kontrolleri (ESC ile çıkış, Oklarla gezinme)
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!isFullScreen) return;
      if (e.key === 'Escape') setIsFullScreen(false);
      if (e.key === 'ArrowRight') nextItem();
      if (e.key === 'ArrowLeft') prevItem();
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [isFullScreen, nextItem, prevItem]);

  return (
    <div className="flex flex-col min-h-screen bg-slate-950 font-sans selection:bg-amber-500/30">

      {/* ARKA PLAN */}
      <div className="fixed inset-0 z-0">
        <img 
          src="/images/foto.jpeg"
          alt="Arka Plan"
          className="w-full h-full object-cover opacity-20 blur-sm" 
        />
        <div className="absolute inset-0 bg-gradient-to-b from-slate-950 via-slate-900/90 to-slate-950" />
      </div>

      <div className="relative z-10 flex flex-col min-h-screen">
        <Header />

        {/* --- MAIN CONTENT --- */}
        <main className="flex-grow flex flex-col justify-center pt-28 pb-10 px-4 md:px-0">

          <div className="container mx-auto max-w-6xl">

            {/* Başlık */}
            <div className="text-center mb-8">
              <h1 className="text-3xl md:text-5xl font-serif text-white mb-2">Ziyaretçi Gözünden</h1>
              <div className="h-1 w-12 bg-amber-500 mx-auto rounded-full"></div>
            </div>

            {/* --- GALERİ ALANI (SİNEMATİK) --- */}
            {/* Tıklanabilir alan: cursor-zoom-in ekledik */}
            <div 
                className="relative w-full aspect-[4/3] md:aspect-[21/9] bg-black/50 rounded-2xl overflow-hidden shadow-2xl border border-white/5 group cursor-zoom-in"
                onClick={() => setIsFullScreen(true)}
            >

              {galleryItems.length > 0 && currentItem ? (
                <>
                  {/* Ana Görsel */}
                  <img
                    key={currentItem.id}
                    src={currentItem.image}
                    alt="Galeri Görseli"
                    className="w-full h-full object-cover animate-fade-in transition-transform duration-[10s] ease-linear transform hover:scale-105"
                  />

                  {/* Overlay Gradient */}
                  <div className="absolute inset-0 bg-gradient-to-t from-black via-black/20 to-transparent opacity-90 pointer-events-none" />

                  {/* Büyütme İkonu (Hover'da görünür) */}
                  <div className="absolute top-4 right-4 bg-black/40 text-white/70 p-2 rounded-full opacity-0 group-hover:opacity-100 transition-opacity backdrop-blur-sm pointer-events-none">
                      <Maximize2 size={24} />
                  </div>

                  {/* İçerik (Metin) */}
                  <div className="absolute bottom-0 left-0 w-full p-8 md:p-12 text-center md:text-left animate-slide-up pointer-events-none">
                    <div className="max-w-4xl mx-auto md:mx-0">
                       <Quote className="text-amber-500 w-8 h-8 mb-4 opacity-80 mx-auto md:mx-0" />
                       <p className="text-xl md:text-3xl font-serif text-slate-100 leading-relaxed italic opacity-90">
                         "{currentItem.message}"
                       </p>
                    </div>
                  </div>

                  {/* Yön Tuşları (Görsel Üzerinde - StopPropagation ile modal açılmasını engeller) */}
                  <button onClick={prevItem} className="absolute left-4 top-1/2 -translate-y-1/2 w-12 h-12 flex items-center justify-center rounded-full bg-black/30 text-white/50 hover:bg-amber-500 hover:text-white hover:scale-110 transition-all backdrop-blur-sm border border-white/5 z-20">
                    <ChevronLeft size={28} />
                  </button>
                  <button onClick={nextItem} className="absolute right-4 top-1/2 -translate-y-1/2 w-12 h-12 flex items-center justify-center rounded-full bg-black/30 text-white/50 hover:bg-amber-500 hover:text-white hover:scale-110 transition-all backdrop-blur-sm border border-white/5 z-20">
                    <ChevronRight size={28} />
                  </button>
                </>
              ) : (
                <div className="w-full h-full flex items-center justify-center text-slate-500">
                  Henüz görsel yok.
                </div>
              )}
            </div>

            {/* --- THUMBNAIL ŞERİDİ & YÜKLEME BUTONU --- */}
            <div className="mt-8 flex items-center justify-center gap-4 px-2">
                <button 
                  onClick={() => setShowUploadModal(true)}
                  className="flex-shrink-0 w-16 h-16 md:w-24 md:h-16 rounded-lg border border-dashed border-white/20 hover:border-amber-500 hover:bg-white/5 flex flex-col items-center justify-center text-slate-400 hover:text-amber-500 transition-all"
                >
                  <Plus size={24} />
                </button>

                <div className="flex gap-3 overflow-x-auto pb-2 scrollbar-hide max-w-[calc(100%-80px)]">
                  {galleryItems.map((item, index) => (
                    <div
                      key={item.id}
                      onClick={() => setCurrentIndex(index)}
                      className={`relative flex-shrink-0 w-24 h-16 md:w-32 md:h-20 rounded-lg overflow-hidden cursor-pointer transition-all duration-300 ${
                        currentIndex === index 
                          ? 'ring-2 ring-amber-500 opacity-100 scale-105' 
                          : 'opacity-40 hover:opacity-80'
                      }`}
                    >
                      <img src={item.image} alt="Thumbnail" className="w-full h-full object-cover" />
                    </div>
                  ))}
                </div>
            </div>
          </div>
        </main>
      </div>

      {/* --- TAM EKRAN (LIGHTBOX) MODAL --- */}
      {isFullScreen && (
        <div 
            className="fixed inset-0 z-[100] bg-black/95 backdrop-blur-md flex items-center justify-center animate-fade-in"
            onClick={() => setIsFullScreen(false)} // Arka plana basınca kapat
        >
            {/* Kapat Butonu */}
            <button 
                onClick={() => setIsFullScreen(false)} 
                className="absolute top-6 right-6 text-white/50 hover:text-white z-50 p-2 bg-white/10 rounded-full transition-colors"
            >
                <X size={32} />
            </button>

            {/* Büyük Resim */}
            <div className="relative w-full h-full max-w-7xl max-h-[90vh] flex flex-col items-center justify-center p-4">
                <img 
                    src={currentItem.image} 
                    alt="Tam Ekran"
                    className="max-w-full max-h-[80vh] object-contain shadow-2xl rounded-sm"
                    onClick={(e) => e.stopPropagation()} // Resme basınca kapanmasın
                />
                <p className="mt-6 text-slate-300 text-lg md:text-xl font-serif italic text-center max-w-3xl" onClick={(e) => e.stopPropagation()}>
                    "{currentItem.message}"
                </p>
            </div>

            {/* Navigasyon (Tam Ekran İçin) */}
            <button onClick={prevItem} className="absolute left-4 top-1/2 -translate-y-1/2 p-4 text-white/30 hover:text-white hover:bg-white/10 rounded-full transition-all">
                <ChevronLeft size={48} />
            </button>
            <button onClick={nextItem} className="absolute right-4 top-1/2 -translate-y-1/2 p-4 text-white/30 hover:text-white hover:bg-white/10 rounded-full transition-all">
                <ChevronRight size={48} />
            </button>
        </div>
      )}

      {/* MODAL (Yükleme) */}
      {showUploadModal && (
        <UploadModal 
          setShowModal={setShowUploadModal} 
          setGalleryItems={setGalleryItems} 
        />
      )}

      {/* STYLES */}
      <style jsx>{`
        @import url('https://fonts.googleapis.com/css2?family=Playfair+Display:ital,wght@0,400;0,600;1,400&family=Inter:wght@300;400;500&display=swap');
        .font-serif { font-family: 'Playfair Display', serif; }
        .font-sans { font-family: 'Inter', sans-serif; }
        .scrollbar-hide::-webkit-scrollbar { display: none; }
        .scrollbar-hide { -ms-overflow-style: none; scrollbar-width: none; }
        @keyframes fade-in { from { opacity: 0; } to { opacity: 1; } }
        .animate-fade-in { animation: fade-in 0.3s ease-out; }
        @keyframes slideUp { from { opacity: 0; transform: translateY(20px); } to { opacity: 1; transform: translateY(0); } }
        .animate-slide-up { animation: slideUp 0.6s ease-out forwards; }
        @keyframes scaleUp { from { opacity: 0; transform: scale(0.95); } to { opacity: 1; transform: scale(1); } }
        .animate-scale-up { animation: scaleUp 0.3s cubic-bezier(0.16, 1, 0.3, 1); }
      `}</style>
    </div>
  );
};

export default UserGalleryPage;
