import React, { useState } from 'react';
import { Menu, X } from 'lucide-react';

const MobileHeader = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const menuItems = [
    { id: 1, text: 'Anasayfa' },
    { id: 2, text: 'Hakkımızda' },
    { id: 3, text: 'Galeri' },
    { id: 4, text: 'Sesli Tur' },
    { id: 5, text: 'Quiz' },
    { id: 6, text: 'Rota' },
  ];

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
    document.body.style.overflow = isMenuOpen ? 'auto' : 'hidden';
  };

  return (
    <>
      {/* Header Bar */}
      <nav className="fixed top-0 left-0 w-full bg-gradient-to-r from-amber-50 to-stone-50 z-[1000] shadow-[0_2px_10px_rgba(120,53,15,0.1)] border-b border-amber-200/30">
        <div className="px-4">
          <div className="flex justify-between items-center h-16 max-w-md mx-auto">

            {/* Title */}
            <div className="flex items-center gap-2">
              <span className="font-['Maven_Pro',sans-serif] font-bold text-amber-900 text-lg">
                Beşirağa Külliyesi
              </span>
            </div>

            {/* Menu Toggle Button */}
            <button 
              className="bg-amber-900 text-white p-2.5 rounded-lg flex items-center justify-center transition-all duration-300 hover:bg-amber-800 shadow-md"
              onClick={toggleMenu}
            >
              {isMenuOpen ? <X size={20} /> : <Menu size={20} />}
            </button>
          </div>
        </div>
      </nav>

      {/* Overlay */}
      {isMenuOpen && (
        <div 
          className="fixed inset-0 bg-black/50 z-[999] transition-opacity duration-300"
          onClick={toggleMenu}
        />
      )}

      {/* Sliding Menu */}
      <div 
        className={`fixed top-0 right-0 w-[280px] h-screen bg-gradient-to-br from-amber-50 via-stone-50 to-amber-100 z-[1001] shadow-2xl transition-transform duration-300 ${
          isMenuOpen ? 'translate-x-0' : 'translate-x-full'
        }`}
      >
        {/* Menu Header */}
        <div className="p-6 border-b border-amber-200/50">
          <div className="flex items-center justify-between">
            <h2 className="font-['Maven_Pro',sans-serif] font-bold text-amber-900 text-xl">
              Menü
            </h2>
            <button 
              className="text-amber-800 hover:bg-amber-200/50 p-2 rounded-lg transition-all duration-300"
              onClick={toggleMenu}
            >
              <X size={20} />
            </button>
          </div>
        </div>

        {/* Menu Items */}
        <div className="flex flex-col p-4 gap-2 overflow-y-auto h-[calc(100vh-120px)]">
          {menuItems.map((item, index) => (
            <a 
              key={item.id} 
              href="#" 
              className="font-['Maven_Pro',sans-serif] text-base font-semibold text-amber-900 no-underline p-4 rounded-xl transition-all duration-300 hover:bg-amber-900 hover:text-white hover:shadow-lg hover:translate-x-1"
              onClick={toggleMenu}
              style={{
                animationDelay: `${index * 50}ms`
              }}
            >
              {item.text}
            </a>
          ))}

          {/* Bottom Info */}
          <div className="mt-auto pt-6 pb-2 border-t border-amber-200/50">
            <p className="text-center text-xs text-amber-700 leading-relaxed">
              1744 yılından bu yana<br />
              <span className="font-bold text-amber-900">280 yıllık tarih</span>
            </p>
          </div>
        </div>
      </div>
    </>
  );
};

export default MobileHeader;
