import React, { useEffect, useState, useRef } from 'react';
import { Play, X, Globe, Volume2, Sparkles, Languages } from 'lucide-react';

const PodcastTransition = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [scrollProgress, setScrollProgress] = useState(0);
  const [showLanguageModal, setShowLanguageModal] = useState(false);
  const [hoveredLang, setHoveredLang] = useState(null);
  const sectionRef = useRef(null);

  const languages = [
    { code: 'tr', name: 'T√ºrk√ße', flag: 'üáπüá∑', color: '#E30A17' },
    { code: 'en', name: 'English', flag: 'üá¨üáß', color: '#012169' },
    { code: 'ar', name: 'ÿßŸÑÿπÿ±ÿ®Ÿäÿ©', flag: 'üá∏üá¶', color: '#165B33' }
  ];

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        setIsVisible(entry.isIntersecting);
      },
      { 
        threshold: [0, 0.5, 1],
        rootMargin: "-10% 0px -10% 0px"
      }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => observer.disconnect();
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      if (!sectionRef.current) return;

      const rect = sectionRef.current.getBoundingClientRect();
      const viewportHeight = window.innerHeight;
      const elementMiddle = rect.top + rect.height / 2;
      const viewportMiddle = viewportHeight / 2;
      const distance = Math.abs(elementMiddle - viewportMiddle);
      const maxDistance = (viewportHeight + rect.height) / 2;

      let progress = 1 - (distance / maxDistance);
      progress = Math.min(Math.max(progress, 0), 1);

      setScrollProgress(progress);
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll();
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleLanguageSelect = (langCode) => {
    console.log(`Selected language: ${langCode}`);
    // Burada podcast √ßalma fonksiyonunu ekleyebilirsiniz
    setShowLanguageModal(false);
  };

  return (
    <>
      <section 
        ref={sectionRef} 
        className="w-full min-h-screen flex items-center justify-center relative overflow-hidden"
      >
        {/* Background Image */}
        <div className="absolute inset-0 w-full h-full">
          <img
            src="/images/foto.jpeg"
            alt="Be≈üiraƒüa K√ºlliyesi"
            className="w-full h-full object-cover"
            style={{
              transform: `scale(${1 + scrollProgress * 0.1})`,
              transition: 'transform 0.6s ease-out'
            }}
          />

          {/* Gradient Overlay */}
          <div 
