import React, { useState } from 'react';
import { Upload, X, Image as ImageIcon } from 'lucide-react';
// Header importu kaldırıldı, çünkü dosya sağlanmadı.
// import Header from '../../components/Header/HeaderController';

const UserGalleryPage = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [message, setMessage] = useState('');
  const [previewImage, setPreviewImage] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Örnek veriler (backend gelince burası API'den gelecek)
  const [galleryItems, setGalleryItems] = useState([
    {
      id: 1,
      image: 'https://pbs.twimg.com/media/Gd4zOf6WgAsWgMs?format=jpg&name=small',
      message: 'Sebilin muhteşem detayları beni çok etkiledi. Osmanlı mimarisinin inceliği burada çok net görülüyor.',
    },
    {
      id: 2,
      image: 'https://pbs.twimg.com/media/GdiyWK_XYAA_y49?format=jpg&name=small',
      message: 'Cami içi görülmeye değer!',
    },
    {
      id: 3,
      image: 'https://pbs.twimg.com/media/GdepXjiXEAATeY8?format=jpg&name=small',
      message: 'Medrese avlusunda tarihi hissettim. 280 yıllık bu yapı hala ayakta duruyor.',
    },
    {
      id: 4,
      image: 'https://pbs.twimg.com/media/GdI3sl-W8AAKNYy?format=jpg&name=small',
      message: 'Barok süslemeler harika.',
    },
    {
      id: 5,
      image: 'https://pbs.twimg.com/media/GcafYF4WMAApNxy?format=jpg&name=small',
      message: 'Ailecek geldik, herkese tavsiye ederim.',
    },
    {
      id: 6,
      image: 'https://pbs.twimg.com/media/GcA0bvfXEAAIcr1?format=jpg&name=small',
      message: 'Çeşmenin detayları muhteşem!',
    },
  ]);

  const handleImageSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewImage(reader.result);
        setSelectedImage(file);
      };
      reader.readAsDataURL(file);
    } else {
      // Kullanıcı dosya seçmeyi iptal ederse
      setPreviewImage(null);
      setSelectedImage(null);
    }
  };

  const clearImage = () => {
    setPreviewImage(null);
    setSelectedImage(null);
    // Dosya girişini de temizle
    const fileInput = document.getElementById('file-upload');
    if (fileInput) fileInput.value = null;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Backend gelince burada API'ye gönderilecek
    if (previewImage && message) {
      const newItem = {
        id: galleryItems.length + 1,
        image: previewImage,
        message: message,
      };
      setGalleryItems([newItem, ...galleryItems]);

      // Formu sıfırla
      clearImage();
      setMessage('');
    }
  };

  const openModal = (item) => {
    setShowModal(item);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <div className="w-full min-h-screen bg-gradient-to-b from-amber-50 to-stone-50 py-12 sm:py-20 px-4">

      {/* Header component'i burada olabilir */}
      {/* <Header /> */}

      {/* Upload Section - Modern 'Post Creator' Stili */}
      <div className="max-w-2xl mx-auto mb-16">
        <form onSubmit={handleSubmit} className="bg-white rounded-xl shadow-lg p-5 sm:p-6 border border-amber-200/30 mt-12">
          <h2 className="text-xl font-semibold text-amber-900 mb-4">Deneyiminizi Paylaşın</h2>

          <textarea
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Mesajınızı yazın..."
            className="w-full px-4 py-3 text-sm border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-amber-500 focus:border-transparent transition-all"
            rows="3"
            required
          />

          {/* Image Preview Alanı */}
          {previewImage && (
            <div className="mt-4 relative w-full h-48 rounded-lg overflow-hidden border border-gray-200">
              <img
                src={previewImage}
                alt="Preview"
                className="w-full h-full object-cover"
              />
              <button
                type="button"
                onClick={clearImage}
                className="absolute top-2 right-2 w-8 h-8 bg-black/60 rounded-full flex items-center justify-center text-white hover:bg-black/80 transition-all"
                title="Resmi kaldır"
              >
                <X size={18} />
              </button>
            </div>
          )}

          {/* Form Kontrolleri */}
          <div className="flex justify-between items-center mt-4">
            <label className="flex items-center gap-2 text-amber-700 hover:text-amber-900 font-medium cursor-pointer transition-colors">
              <ImageIcon className="w-5 h-5" />
              <span>{previewImage ? 'Resmi Değiştir' : 'Resim Ekle'}</span>
              <input
                id="file-upload"
                type="file"
                className="hidden"
                accept="image/*"
                onChange={handleImageSelect}
              />
            </label>

            <button
              type="submit"
              disabled={!previewImage || !message}
              className="px-5 py-2.5 bg-amber-900 text-white rounded-lg font-semibold flex items-center justify-center gap-2 hover:bg-amber-800 transition-colors disabled:bg-gray-300 disabled:cursor-not-allowed"
              title="Paylaş"
            >
              <span>Paylaş</span>
              {/* Orijinal ikon (Paper Airplane) */}
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
              </svg>
            </button>
          </div>
        </form>
      </div>

      {/* Gallery Section - Masonry Grid (Bu kısım zaten modern ve iyiydi) */}
      <div className="max-w-7xl mx-auto">
        <h2 className="text-3xl sm:text-4xl font-bold text-gray-900 mb-8 text-center">
          Ziyaretçilerimizden Kareler
        </h2>

        <div className="columns-1 sm:columns-2 lg:columns-3 xl:columns-4 gap-4 space-y-4">
          {galleryItems.map((item) => (
            <div
              key={item.id}
              className="break-inside-avoid mb-4 group cursor-pointer"
              onClick={() => openModal(item)}
            >
              <div className="bg-white rounded-xl overflow-hidden shadow-md hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1">
                <div className="relative overflow-hidden">
                  <img
                    src={item.image}
                    alt="User submission"
                    className="w-full h-auto object-cover group-hover:scale-110 transition-transform duration-300"
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                </div>
                <div className="p-4">
                  <p className="text-gray-700 text-sm leading-relaxed line-clamp-3">
                    {item.message}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Modal for Full Image - Modern Split-View */}
      {showModal && (
        <div
          className="fixed inset-0 z-50 flex items-center justify-center bg-black/80 p-4"
          onClick={closeModal}
        >
          <div
            className="relative max-w-5xl w-full bg-white rounded-2xl overflow-hidden shadow-2xl flex flex-col lg:flex-row"
            onClick={(e) => e.stopPropagation()}
          >
            <button
              onClick={closeModal}
              className="absolute top-3 right-3 lg:top-4 lg:right-4 w-10 h-10 bg-white/70 backdrop-blur-sm rounded-full flex items-center justify-center text-gray-800 hover:bg-white transition-all z-10"
              title="Kapat"
            >
              <X size={24} />
            </button>

            {/* Resim Alanı */}
            <div className="w-full lg:w-2/3 bg-black/5 flex items-center justify-center">
              <img
                src={showModal.image}
                alt="Full view"
                className="w-full h-auto max-h-[50vh] lg:max-h-[80vh] object-contain"
              />
            </div>

            {/* Mesaj Alanı */}
            <div className="w-full lg:w-1/3 p-6 lg:p-8 flex flex-col justify-center">
              <h3 className="text-lg font-semibold text-amber-900 mb-3">Ziyaretçi Mesajı:</h3>
              <p className="text-gray-700 text-base leading-relaxed overflow-y-auto">
                {showModal.message}
              </p>
            </div>
          </div>
        </div>
      )}

    </div>
  );
};

export default UserGalleryPage;
