// Gerekli kütüphaneler:
// npm install framer-motion lucide-react
import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
// Menu ve X ikonlarını geri ekledik
import { Home, LayoutGrid, Headphones, HelpCircle, Info, Menu, X } from 'lucide-react';

// Menü listemiz (ikon ve etiket içerir)
const menuItems = [
  { id: 1, Icon: Home, href: '#', label: 'Anasayfa' },
  { id: 2, Icon: LayoutGrid, href: '#', label: 'Galeri' },
  { id: 3, Icon: Headphones, href: '#', label: 'Sesli Tur' },
  { id: 4, Icon: HelpCircle, href: '#', label: 'Bilgi Yarışması' },
  { id: 5, Icon: Info, href: '#', label: 'Hakkımızda' }
];

// Masaüstü Header'ı için scroll animasyonu
const desktopHeaderVariants = {
  top: {
    backgroundColor: 'rgba(255, 255, 255, 0)',
    paddingTop: '2rem',
    paddingBottom: '2rem',
  },
  scrolled: {
    backgroundColor: 'rgba(255, 255, 255, 0)',
    paddingTop: '1rem',
    paddingBottom: '1rem',
  },
};

// Mobil açılır menü için animasyon
const mobileMenuVariant = {
  hidden: {
    opacity: 0,
    y: -20, // Yukarıdan aşağı doğru hafifçe kay
    transition: { duration: 0.2, ease: 'easeOut' }
  },
  visible: {
    opacity: 1,
    y: 0,
    transition: { 
      duration: 0.2, 
      ease: 'easeIn',
      staggerChildren: 0.05 // Linkleri kademeli getir
    }
  },
};

// Menü linkleri için animasyon
const mobileItemVariant = {
  hidden: { opacity: 0, y: -10 },
  visible: { opacity: 1, y: 0 }
};

// --- Ana Bileşen ---

const Header = () => {
  // Masaüstü state'leri
  const [isScrolled, setIsScrolled] = useState(false);
  const [hoveredId, setHoveredId] = useState(null);

  // Mobil state
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const toggleMenu = () => setIsMenuOpen(!isMenuOpen);

  // Scroll dinleyicisi (Sadece Masaüstü için)
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    const mediaQuery = window.matchMedia('(min-width: 768px)');

    if (mediaQuery.matches) {
      window.addEventListener('scroll', handleScroll);
      // Mobilde menü açıksa ve ekran büyürse kapansın
      if(isMenuOpen) setIsMenuOpen(false);
    }

    return () => window.removeEventListener('scroll', handleScroll);
  }, [isMenuOpen]); // isMenuOpen'i de dinle

  // Mobil menü açıkken body scroll'u kilitle
  useEffect(() => {
    document.body.style.overflow = isMenuOpen ? 'hidden' : 'auto';
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, [isMenuOpen]);

  return (
    <>
      {/* =================================
        1. MASAÜSTÜ HEADER (md ve üzeri)
        =================================
      */}
      <motion.nav
        className="w-full fixed top-0 left-0 z-[1000] hidden md:block" // Sadece md ve üzerinde görünür
        variants={desktopHeaderVariants}
        animate={isScrolled ? 'scrolled' : 'top'}
        transition={{ duration: 0.3, ease: 'easeInOut' }}
      >
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-center items-center h-16">
            <div className="flex items-center space-x-2">
              <ul 
                className="flex items-center space-x-2 relative"
                onMouseLeave={() => setHoveredId(null)}
              >
                {menuItems.map((item) => (
                  <li key={item.id} className="relative">
                    <a
                      href={item.href}
                      className={`
                        relative block p-3 text-lg font-semibold text-amber-900 rounded-lg
                        transition-all duration-300 ease-in-out
                        ${isScrolled 
                          ? 'bg-amber-50/95 backdrop-blur-md shadow-lg border border-amber-200' 
                          : 'bg-transparent shadow-none border-transparent'
                        }
                      `}
                      onMouseEnter={() => setHoveredId(item.id)}
                      aria-label={item.label}
                    >
                      {hoveredId === item.id && (
                        <motion.div
                          layoutId="magic-ink" 
                          className="absolute inset-0 bg-amber-800 rounded-lg z-0"
                          transition={{ type: 'spring', stiffness: 300, damping: 25 }}
                        />
                      )}
                      <span className={`relative z-10 transition-colors ${hoveredId === item.id ? 'text-white' : ''}`}>
                        <item.Icon size={24} />
                      </span>
                    </a>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </motion.nav>

      {/* =================================
        2. MOBİL HEADER (md'den küçük)
        =================================
      */}
      <div className="fixed top-5 left-0 w-full z-[1000] md:hidden px-4">
        <div className="relative flex flex-col items-center">

          {/* Hamburger Butonu */}
          <button 
            className="p-2 cursor-pointer text-amber-800 flex items-center justify-center rounded transition-all duration-300 z-[1001] hover:bg-amber-100 rounded shadow-[0_5px_15px_rgba(0,0,0,0.15)] bg-amber-50 border border-amber-200"
            onClick={toggleMenu}
            aria-label="Menüyü aç/kapat"
          >
            {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>

          {/* Açılır Menü Alanı */}
          <AnimatePresence>
            {isMenuOpen && (
              <motion.div
                className="
                  absolute top-full mt-2 w-full max-w-xs
                  bg-amber-50/95 backdrop-blur-md 
                  rounded-lg shadow-xl border border-amber-200
                  overflow-hidden
                "
                variants={mobileMenuVariant}
                initial="hidden"
                animate="visible"
                exit="hidden"
              >
                <motion.ul
                  className="flex flex-col p-2"
                  variants={mobileMenuVariant} // staggerChildren için
                >
                  {menuItems.map((item) => (
                    <motion.li key={item.id} variants={mobileItemVariant}>
                      <a
                        href={item.href}
                        onClick={toggleMenu} // Tıklayınca menüyü kapat
                        className="flex items-center gap-3 p-3 text-lg font-semibold text-amber-900 rounded-md transition-colors duration-200 hover:bg-amber-100"
                      >
                        <item.Icon size={20} />
                        <span>{item.label}</span>
                      </a>
                    </motion.li>
                  ))}
                </motion.ul>
              </motion.div>
            )}
          </AnimatePresence>

        </div>
      </div>

      {/* Alttaki bar tamamen kaldırıldı. */}
    </>
  );
};

export default Header;
