import React, { useEffect, useState, useRef } from 'react';

const ConceptLaunch = () => {
  const [isInView, setIsInView] = useState(false);
  const [scale, setScale] = useState(1);
  const sectionRef = useRef(null);

  useEffect(() => {
    // Section'ın görünür olup olmadığını kontrol et
    const observer = new IntersectionObserver(
      ([entry]) => {
        setIsInView(entry.isIntersecting);
      },
      { threshold: 0.5 }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => observer.disconnect();
  }, []);

  useEffect(() => {
    // Sadece görünür olduktan sonra scroll'u dinle
    if (!isInView) return;

    const handleScroll = () => {
      const scrolled = window.scrollY;
      const newScale = 1 + (scrolled * 0.003); // Büyüme hızı
      setScale(Math.min(newScale, 20)); // Maximum 20 kat büyüme
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [isInView]);

  return (
    <div 
      ref={sectionRef}
      className="w-full min-h-screen flex items-center justify-center flex-col px-4 sm:px-6 lg:px-8 py-12 sm:py-16 lg:py-20 transition-colors duration-700"
      style={{
        backgroundColor: isInView ? `rgba(120, 53, 15, ${(scale - 1) / 19})` : '#ffffff'
      }}
    >
      <div className="max-w-7xl w-full flex flex-col gap-8 sm:gap-10 lg:gap-12 transition-opacity duration-500"
           style={{ opacity: isInView ? 1 : 0 }}>

        {/* Title Section */}
        <div className="space-y-4">
          <h1 className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl xl:text-8xl font-semibold leading-tight text-gray-900 font-['Maven_Pro',sans-serif] transition-colors duration-500"
              style={{ 
                color: isInView && scale > 2 ? '#ffffff' : '#000000'
              }}>
            Beşirağa Külliyesi'nde
            <br />
            Zaman Yolculuğu <span 
              className="inline-block transition-colors duration-500"
              style={{ 
                color: isInView && scale > 2 ? '#fbbf24' : '#92400e'
              }}>
              280 Yıllık
              <br />
              Bir Miras
            </span>
          </h1>
        </div>

        {/* Description Section */}
        <div className="max-w-4xl">
          <p className="text-lg sm:text-xl md:text-2xl lg:text-3xl leading-relaxed font-normal font-['Inter',sans-serif] transition-colors duration-500"
             style={{ 
               color: isInView && scale > 2 ? 'rgba(255, 255, 255, 0.9)' : '#4b5563'
             }}>
            1744 yılından günümüze, Hacı Beşir Ağa'nın emaneti olan bu eşsiz külliye, 
            Osmanlı mimarisinde Batı barok üslubunun ilk örneklerinden biri olarak 
            tarihe tanıklık ediyor. Cami, medrese, kütüphane, sebil ve tekke ile 
            bir bütün olarak tasarlanan bu mimari şaheser, İstanbul'un kültürel 
            zenginliğinin bir parçası.
          </p>
        </div>

        {/* Stats Section */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 sm:gap-6 mt-8">

          <div className="flex flex-col items-center justify-center p-6 sm:p-8 bg-white/5 backdrop-blur-sm rounded-2xl border transition-all duration-500 hover:scale-105"
               style={{
                 borderColor: isInView && scale > 2 ? 'rgba(251, 191, 36, 0.3)' : 'rgba(0, 0, 0, 0.1)',
                 backgroundColor: isInView && scale > 2 ? 'rgba(255, 255, 255, 0.1)' : 'rgba(255, 255, 255, 0.8)'
               }}>
            <span className="text-3xl sm:text-4xl lg:text-5xl font-bold mb-2 transition-colors duration-500"
                  style={{ 
                    color: isInView && scale > 2 ? '#fbbf24' : '#92400e'
                  }}>
              1744
            </span>
            <span className="text-sm sm:text-base font-medium transition-colors duration-500"
                  style={{ 
                    color: isInView && scale > 2 ? '#ffffff' : '#6b7280'
                  }}>
              Yapım Yılı
            </span>
          </div>

          <div className="flex flex-col items-center justify-center p-6 sm:p-8 bg-white/5 backdrop-blur-sm rounded-2xl border transition-all duration-500 hover:scale-105"
               style={{
                 borderColor: isInView && scale > 2 ? 'rgba(251, 191, 36, 0.3)' : 'rgba(0, 0, 0, 0.1)',
                 backgroundColor: isInView && scale > 2 ? 'rgba(255, 255, 255, 0.1)' : 'rgba(255, 255, 255, 0.8)'
               }}>
            <span className="text-3xl sm:text-4xl lg:text-5xl font-bold mb-2 transition-colors duration-500"
                  style={{ 
                    color: isInView && scale > 2 ? '#fbbf24' : '#92400e'
                  }}>
              5
            </span>
            <span className="text-sm sm:text-base font-medium transition-colors duration-500"
                  style={{ 
                    color: isInView && scale > 2 ? '#ffffff' : '#6b7280'
                  }}>
              Bina
            </span>
          </div>

          <div className="flex flex-col items-center justify-center p-6 sm:p-8 bg-white/5 backdrop-blur-sm rounded-2xl border transition-all duration-500 hover:scale-105"
               style={{
                 borderColor: isInView && scale > 2 ? 'rgba(251, 191, 36, 0.3)' : 'rgba(0, 0, 0, 0.1)',
                 backgroundColor: isInView && scale > 2 ? 'rgba(255, 255, 255, 0.1)' : 'rgba(255, 255, 255, 0.8)'
               }}>
            <span className="text-3xl sm:text-4xl lg:text-5xl font-bold mb-2 transition-colors duration-500"
                  style={{ 
                    color: isInView && scale > 2 ? '#fbbf24' : '#92400e'
                  }}>
              280
            </span>
            <span className="text-sm sm:text-base font-medium transition-colors duration-500"
                  style={{ 
                    color: isInView && scale > 2 ? '#ffffff' : '#6b7280'
                  }}>
              Yıllık Tarih
            </span>
          </div>

          <div className="flex flex-col items-center justify-center p-6 sm:p-8 bg-white/5 backdrop-blur-sm rounded-2xl border transition-all duration-500 hover:scale-105"
               style={{
                 borderColor: isInView && scale > 2 ? 'rgba(251, 191, 36, 0.3)' : 'rgba(0, 0, 0, 0.1)',
                 backgroundColor: isInView && scale > 2 ? 'rgba(255, 255, 255, 0.1)' : 'rgba(255, 255, 255, 0.8)'
               }}>
            <span className="text-3xl sm:text-4xl lg:text-5xl font-bold mb-2 transition-colors duration-500"
                  style={{ 
                    color: isInView && scale > 2 ? '#fbbf24' : '#92400e'
                  }}>
              ⭐
            </span>
            <span className="text-sm sm:text-base font-medium transition-colors duration-500"
                  style={{ 
                    color: isInView && scale > 2 ? '#ffffff' : '#6b7280'
                  }}>
              İlk Barok
            </span>
          </div>

        </div>
      </div>
    </div>
  );
};

export default ConceptLaunch;
