// Gerekli kütüphaneler:
// npm install framer-motion lucide-react
import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
// İkonları tek bir yerden alıyoruz
import { Home, LayoutGrid, Headphones, HelpCircle, Info, Menu, X } from 'lucide-react';

// 1. Masaüstü Menü Verisi (İkonlar)
const desktopMenuItems = [
  { id: 1, Icon: Home, href: '#', label: 'Anasayfa' },
  { id: 2, Icon: LayoutGrid, href: '#', label: 'Galeri' },
  { id: 3, Icon: Headphones, href: '#', label: 'Sesli Tur' },
  { id: 4, Icon: HelpCircle, href: '#', label: 'Bilgi Yarışması' },
  { id: 5, Icon: Info, href: '#', label: 'Hakkımızda' }
];

// 2. Mobil Menü Verisi (Metinler)
const mobileMenuItems = [
    { id: 1, text: 'Anasayfa', href: '#' },
    { id: 2, text: 'Ziyaretçi Galerisi', href: '#' },
    { id: 3, text: 'Sesli Keşif', href: '#' },
    { id: 4, text: 'Beşir Ağa Testi', href: '#' },
    { id: 5, text: 'Hakkımızda', href: '#' }
];

// --- Animasyon Varyantları ---

// A. Masaüstü Header'ı için
const desktopHeaderVariants = {
  top: {
    backgroundColor: 'rgba(255, 255, 255, 0)',
    paddingTop: '2rem',
    paddingBottom: '2rem',
  },
  scrolled: {
    backgroundColor: 'rgba(255, 255, 255, 0)',
    paddingTop: '1rem',
    paddingBottom: '1rem',
  },
};

// B. Mobil Tam Ekran Menü için
const mobileMenuVariant = {
  hidden: {
    opacity: 0,
    y: '-100%',
    transition: { duration: 0.3, ease: 'easeIn' }
  },
  visible: {
    opacity: 1,
    y: 0,
    transition: { duration: 0.4, ease: 'easeOut' }
  },
};

// C. Mobil Menü Linklerinin Kademeli Girişi için
const mobileListVariant = {
  hidden: {},
  visible: {
    transition: {
      staggerChildren: 0.08, // Her link arası 0.08sn bekle
      delayChildren: 0.2,    // Animasyona başlamadan 0.2sn bekle
    }
  }
};

const mobileItemVariant = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0 }
};


// --- Ana Bileşen ---

const Header = () => {
  // Masaüstü state'leri
  const [isScrolled, setIsScrolled] = useState(false);
  const [hoveredId, setHoveredId] = useState(null);

  // Mobil state
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Scroll dinleyicisi (Sadece Masaüstü için)
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    // Sadece md (768px) üzeri ekranlarda scroll dinle
    const mediaQuery = window.matchMedia('(min-width: 768px)');

    if (mediaQuery.matches) {
      window.addEventListener('scroll', handleScroll);
    }

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Mobil menü açıldığında body scroll'u kilitlemek için
  useEffect(() => {
    if (isMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'auto';
    }

    // Cleanup
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, [isMenuOpen]);

  // Mobil menü toggle fonksiyonu
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <>
      {/* =================================
        1. MASAÜSTÜ HEADER (md ve üzeri)
        =================================
      */}
      <motion.nav
        className="w-full fixed top-0 left-0 z-[1000] hidden md:block" // Sadece md ve üzerinde görünür
        variants={desktopHeaderVariants}
        animate={isScrolled ? 'scrolled' : 'top'}
        transition={{ duration: 0.3, ease: 'easeInOut' }}
      >
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-center items-center h-16">
            <div className="flex items-center space-x-2">
              <ul 
                className="flex items-center space-x-2 relative"
                onMouseLeave={() => setHoveredId(null)}
              >
                {desktopMenuItems.map((item) => (
                  <li key={item.id} className="relative">
                    <a
                      href={item.href}
                      className={`
                        relative block p-3 text-lg font-semibold text-amber-900 rounded-lg
                        transition-all duration-300 ease-in-out
                        ${isScrolled 
                          ? 'bg-amber-50/95 backdrop-blur-md shadow-lg border border-amber-200' 
                          : 'bg-transparent shadow-none border-transparent'
                        }
                      `}
                      onMouseEnter={() => setHoveredId(item.id)}
                      aria-label={item.label}
                    >
                      {hoveredId === item.id && (
                        <motion.div
                          layoutId="magic-ink" 
                          className="absolute inset-0 bg-amber-800 rounded-lg z-0"
                          transition={{ type: 'spring', stiffness: 300, damping: 25 }}
                        />
                      )}
                      <span className={`relative z-10 transition-colors ${hoveredId === item.id ? 'text-white' : ''}`}>
                        <item.Icon size={24} />
                      </span>
                    </a>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </motion.nav>

      {/* =================================
        2. MOBİL HEADER (md'den küçük)
        =================================
      */}
      <nav className="fixed top-5 left-0 w-full z-[1000] md:hidden"> {/* Sadece md altında görünür */}
        <div className="px-4">
          <div className="flex justify-center items-center h-16">
            {/* Menu Toggle Button */}
            <button 
              className=" p-2 cursor-pointer text-amber-800 flex items-center justify-center rounded transition-all duration-300 z-[1001] hover:bg-amber-100 rounded shadow-[0_5px_15px_rgba(0,0,0,0.15)] bg-amber-50 border border-amber-200"
              onClick={toggleMenu}
              aria-label="Menüyü aç/kapat"
            >
              {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>
      </nav>

      {/* Mobil Açılır Menü (Framer Motion ile) */}
      <AnimatePresence>
        {isMenuOpen && (
          <motion.div 
            className="fixed top-0 left-0 w-full h-screen bg-gradient-to-br from-amber-50 via-stone-50 to-amber-100 z-[1000] md:hidden"
            variants={mobileMenuVariant}
            initial="hidden"
            animate="visible"
            exit="hidden"
          >
            {/* Menü Linkleri (Kademeli animasyon) */}
            <motion.div 
              className="flex flex-col items-center justify-center gap-6 pt-20 px-8 pb-8 h-full"
              variants={mobileListVariant}
              initial="hidden"
              animate="visible"
            >
              {mobileMenuItems.map((item) => (
                <motion.a 
                  key={item.id} 
                  href={item.href}
                  className="font-['Maven_Pro',sans-serif] text-xl font-semibold text-amber-900 no-underline py-2.5 px-5 rounded-lg transition-all duration-300 w-full text-center hover:bg-amber-200/50"
                  onClick={toggleMenu} // Linke tıklayınca menüyü kapat
                  variants={mobileItemVariant} // Her linke tek tek animasyon ver
                >
                  {item.text}
                </motion.a>
              ))}
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </>
  );
};

export default Header;
