import React, { useEffect, useState } from 'react';

const Hero = () => {
  const [scroll, setScroll] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const scrolled = window.scrollY;
      setScroll(scrolled);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div 
      className="w-full py-[12%] sm:py-24 md:py-32 lg:py-[12%] flex items-center justify-center relative transition-all duration-300 overflow-hidden"
      style={{
        transform: `scale(${1 + scroll * 0.001})`,
        opacity: 1 - scroll * 0.002,
        willChange: 'transform, opacity'
      }}
    >
      {/* SVG Pattern - Right Bottom Corner */}
      <div 
        className="absolute -right-20 -bottom-20 w-[600px] h-[600px] sm:w-[800px] sm:h-[800px] md:w-[1000px] md:h-[1000px] lg:w-[1200px] lg:h-[1200px] opacity-30 pointer-events-none"
        style={{
          backgroundImage: 'url(https://www.onder.org.tr/build/assets/search-bg-842c8fc7.svg)',
          backgroundSize: 'contain',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'bottom right'
        }}
      ></div>

      {/* Decorative Background Pattern */}
      <div className="absolute inset-0 opacity-5 pointer-events-none">
        <div className="absolute top-10 left-10 w-64 h-64 bg-amber-800 rounded-full blur-3xl"></div>
        <div className="absolute bottom-10 right-10 w-96 h-96 bg-stone-800 rounded-full blur-3xl"></div>
      </div>

      <div className="max-w-7xl w-full text-center flex flex-col gap-8 sm:gap-10 md:gap-12 px-4 sm:px-6 lg:px-8 relative z-10 pt-16">

        {/* Title Section */}
        <h1 className="text-[2rem] sm:text-5xl md:text-6xl lg:text-7xl xl:text-7xl font-bold leading-tight m-0 font-['Maven_Pro',sans-serif]">
          <span className="text-gray-900">Osmanlı Mimarisinin</span>
          <br />
          <span className="text-gray-900">İncisi,</span>
          <span 
            className="bg-gradient-to-r from-amber-800 via-amber-700 to-amber-900 bg-clip-text text-transparent font-bold pl-2"
            style={{
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent'
            }}
          >
            Beşirağa Külliyesi
          </span>
        </h1>

        {/* Subtitle */}
        <p className="text-lg sm:text-xl md:text-2xl text-gray-600 max-w-3xl mx-auto leading-relaxed font-light">
          1744-1745 yıllarında Hacı Beşir Ağa tarafından yaptırılan, Türk mimarisinde Batı barok üslubunun ilk ve en değerli örneklerinden
        </p>

        {/* Year Badge */}
        <div className="flex items-center justify-center gap-3">
          <div className="px-6 py-3 text-amber-700 transition-all duration-300 hover:scale-105">
            <span className="text-sm sm:text-base font-bold tracking-widest">
              EST. 1744 • 280 YIL
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Hero;
