/**
 * Dosya: frontend/src/pages/RezervasyonPage/index.jsx
 * Açıklama: Çok adımlı rezervasyon formu ana sayfası - Car IQ Pay stili
 */

import React, { useState } from 'react';
import Step1KurumBilgileri from '../../components/Rezervasyon/Step1KurumBilgileri';
import Step2TarihSecimi from '../../components/Rezervasyon/Step2TarihSecimi';
import Step3SaatSecimi from '../../components/Rezervasyon/Step3SaatSecimi';
import Step4MekanSecimi from '../../components/Rezervasyon/Step4MekanSecimi';
import Step5Aciklama from '../../components/Rezervasyon/Step5Aciklama';
import Step6OgrenciBilgileri from '../../components/Rezervasyon/Step6OgrenciBilgileri';

const RezervasyonPage = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    // Adım 1: Kurum Bilgileri
    yetkili_ad_soyad: '',
    kurum_adi: '',
    kurum_tipi: 'okul',
    il: '',
    ilce: '',
    telefon: '',

    // Adım 2: Tarih
    tarih: '',

    // Adım 3: Saat
    saat_dilimi: '',

    // Adım 4: Mekanlar
    mekanlar: {},

    // Adım 5: Açıklama
    aciklama: '',

    // Adım 6: Öğrenciler
    ogrenciler: []
  });

  const totalSteps = 6;

  const steps = [
    { id: 1, title: 'Başlangıç', subtitle: 'Kurum bilgilerinizi girin' },
    { id: 2, title: 'Tarih seçimi', subtitle: 'Ziyaret tarihini belirleyin' },
    { id: 3, title: 'Saat seçimi', subtitle: 'Ziyaret saatini belirleyin' },
    { id: 4, title: 'Mekan seçimi', subtitle: 'Ziyaret edilecek mekanları seçin' },
    { id: 5, title: 'Açıklama', subtitle: 'Ek bilgiler ekleyin' },
    { id: 6, title: 'Rezervasyonu tamamla', subtitle: 'Öğrenci bilgilerini girin' }
  ];

  const nextStep = () => {
    if (currentStep < totalSteps) {
      setCurrentStep(currentStep + 1);
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  const updateFormData = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return (
          <Step1KurumBilgileri
            formData={formData}
            updateFormData={updateFormData}
            nextStep={nextStep}
          />
        );
      case 2:
        return (
          <Step2TarihSecimi
            formData={formData}
            updateFormData={updateFormData}
            nextStep={nextStep}
            prevStep={prevStep}
          />
        );
      case 3:
        return (
          <Step3SaatSecimi
            formData={formData}
            updateFormData={updateFormData}
            nextStep={nextStep}
            prevStep={prevStep}
          />
        );
      case 4:
        return (
          <Step4MekanSecimi
            formData={formData}
            updateFormData={updateFormData}
            nextStep={nextStep}
            prevStep={prevStep}
          />
        );
      case 5:
        return (
          <Step5Aciklama
            formData={formData}
            updateFormData={updateFormData}
            nextStep={nextStep}
            prevStep={prevStep}
          />
        );
      case 6:
        return (
          <Step6OgrenciBilgileri
            formData={formData}
            updateFormData={updateFormData}
            prevStep={prevStep}
          />
        );
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 via-white to-orange-50">
      {/* Main Content Container */}
      <div className="max-w-7xl mx-auto px-4 py-8">
        <div className="overflow-hidden min-h-[600px]">
          <div className="flex flex-col lg:flex-row">

            {/* Left Sidebar - Step Navigation */}
            <div className="lg:w-80 border-r border-gray-200 p-8">
              <div className="mb-8">
                <h2 className="text-xl font-bold text-gray-900 mb-2">
                  Rezervasyon formu ile başlayın
                </h2>
                <p className="text-sm text-gray-600">
                  Formu doldurmak sadece birkaç dakika sürer
                </p>
              </div>

              {/* Step List */}
              <div className="space-y-1">
                {steps.map((step) => (
                  <div
                    key={step.id}
                    className={`relative pl-6 py-4 cursor-pointer transition-all duration-200 rounded-lg ${
                      step.id === currentStep
                        ? 'bg-amber-50 border-l-4 border-amber-500'
                        : step.id < currentStep
                        ? 'bg-green-50 border-l-4 border-green-500'
                        : 'border-l-4 border-transparent hover:bg-gray-50'
                    }`}
                    onClick={() => step.id < currentStep && setCurrentStep(step.id)}
                  >
                    {/* Step Indicator */}
                    <div className="absolute left-2 top-1/2 -translate-y-1/2">
                      {step.id < currentStep ? (
                        <div className="w-5 h-5 rounded-full bg-green-500 flex items-center justify-center">
                          <svg className="w-3 h-3 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                          </svg>
                        </div>
                      ) : (
                        <div
                          className={`w-5 h-5 rounded-full flex items-center justify-center text-xs font-bold ${
                            step.id === currentStep
                              ? 'bg-amber-500 text-white'
                              : 'bg-gray-200 text-gray-500'
                          }`}
                        >
                          {step.id}
                        </div>
                      )}
                    </div>

                    {/* Step Content */}
                    <div className="ml-6">
                      <p
                        className={`font-semibold text-sm ${
                          step.id === currentStep
                            ? 'text-amber-600'
                            : step.id < currentStep
                            ? 'text-green-600'
                            : 'text-gray-600'
                        }`}
                      >
                        {step.title}
                      </p>
                      <p className="text-xs text-gray-500 mt-0.5">{step.subtitle}</p>
                    </div>
                  </div>
                ))}
              </div>

              {/* Progress Info */}
              <div className="mt-8 p-4 bg-amber-50 rounded-lg border border-amber-200">
                <div className="flex items-center justify-between mb-2">
                  <span className="text-sm font-medium text-gray-700">İlerleme</span>
                  <span className="text-sm font-bold text-amber-600">
                    {Math.round((currentStep / totalSteps) * 100)}%
                  </span>
                </div>
                <div className="w-full bg-amber-200 rounded-full h-2">
                  <div
                    className="bg-gradient-to-r from-amber-500 to-orange-500 h-2 rounded-full transition-all duration-300"
                    style={{ width: `${(currentStep / totalSteps) * 100}%` }}
                  />
                </div>
                <p className="text-xs text-gray-600 mt-2">
                  Adım {currentStep} / {totalSteps}
                </p>
              </div>
            </div>

            {/* Right Content - Form Area */}
            <div className="flex-1 p-8 lg:p-12">
              <div className="max-w-3xl">
                {/* Current Step Title */}
                <div className="mb-8">
                  <h2 className="text-3xl font-bold text-gray-900 mb-2">
                    {steps[currentStep - 1].title}
                  </h2>
                  <p className="text-gray-600">{steps[currentStep - 1].subtitle}</p>
                </div>

                {/* Form Content */}
                <div className="form-content">
                  {renderStep()}
                </div>

                {/* Security Notice */}
