// Gerekli kütüphaneler:
// npm install framer-motion lucide-react
import React, { useState, useEffect } from 'react';
import { Menu, X } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

// Menü verisi aynı
const menuItems = [
  { id: 1, text: 'Anasayfa', href: '#' },
  { id: 2, text: 'Galeri', href: '#' },
  { id: 3, text: 'Sesli Tur', href: '#' },
  { id: 4, text: 'Bilgi Yarışması', href: '#' },
  { id: 5, text: 'Hakkımızda', href: '#' }
];

const Header = () => {
  // Sadece scroll durumunu tutar
  const [isScrolled, setIsScrolled] = useState(false);
  // Sadece MOBİL menünün açık/kapalı durumunu tutar
  const [menuOpen, setMenuOpen] = useState(false);
  // Masaüstü hover efekti için
  const [hoveredId, setHoveredId] = useState(null);

  // Scroll dinleyicisi
  useEffect(() => {
    const handleScroll = () => {
      // 50px'ten fazla scroll edildiyse true yap
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    // Component kaldırıldığında dinleyiciyi temizle
    return () => window.removeEventListener('scroll', handleScroll);
  }, []); // Sadece bir kez çalışır

  // Scroll edildiğinde header'ın alacağı stil
  const headerVariants = {
    top: {
      backgroundColor: 'rgba(255, 255, 255, 0)', // Başlangıçta şeffaf
      paddingTop: '2rem',
      paddingBottom: '2rem',
    },
    scrolled: {
      backgroundColor: 'rgba(255, 251, 235, 0.95)', // amber-50'nin hafif transparanı
      paddingTop: '1rem',
      paddingBottom: '1rem',
      backdropFilter: 'blur(10px)', // Arka planı bulanıklaştır (modern etki)
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.05)',
      borderBottom: '1px solid #fde68a' // amber-200
    },
  };

  return (
    <motion.nav
      className="w-full fixed top-0 left-0 z-[1000]"
      variants={headerVariants}
      animate={isScrolled ? 'scrolled' : 'top'}
      transition={{ duration: 0.3, ease: 'easeInOut' }}
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">

          {/* Logo veya Site Adı */}
          <div className="flex-shrink-0">
            <a href="#" className="text-2xl font-bold text-amber-900 font-['Maven_Pro',sans-serif]">
              {/* Buraya bir logo veya proje adı (örn: Beşirağa Külliyesi) gelebilir */}
              Site Adı
            </a>
          </div>

          {/* 1. Masaüstü Menü (md ve üzeri) */}
          <div className="hidden md:flex md:items-center md:space-x-2">
            <ul 
              className="flex items-center space-x-2 relative"
              onMouseLeave={() => setHoveredId(null)} // Mouse menüden çıkınca hover'ı temizle
            >
              {menuItems.map((item) => (
                <li key={item.id} className="relative">
                  <a
                    href={item.href}
                    className="relative block px-4 py-2 text-md font-semibold text-amber-900 transition-colors duration-300 hover:text-amber-700 z-10"
                    onMouseEnter={() => setHoveredId(item.id)}
                  >
                    {/* "Magic Ink" Efekti */}
                    {hoveredId === item.id && (
                      <motion.div
                        layoutId="magic-ink" // Bu ID animasyonun takibini sağlar
                        className="absolute inset-0 bg-amber-800 rounded-lg z-0"
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        transition={{ type: 'spring', stiffness: 300, damping: 25 }}
                      />
                    )}
                    {/* Yazının kendisi (hover'da renk değişimi için) */}
                    <span className={`relative z-10 transition-colors ${hoveredId === item.id ? 'text-white' : ''}`}>
                      {item.text}
                    </span>
                  </a>
                </li>
              ))}
            </ul>
          </div>

          {/* 2. Mobil Menü Butonu (md'den küçük) */}
          <div className="md:hidden flex items-center">
            <button
              onClick={() => setMenuOpen(!menuOpen)}
              className="inline-flex items-center justify-center p-2 rounded-md text-amber-800 hover:text-amber-900 hover:bg-amber-100 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-amber-500"
              aria-expanded={menuOpen}
            >
              <span className="sr-only">Menüyü aç</span>
              {menuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>

        </div>
      </div>

      {/* 3. Mobil Menü Açılır Alanı (Animasyonlu) */}
      <AnimatePresence>
        {menuOpen && (
          <motion.div
            className="md:hidden absolute top-full left-0 w-full bg-white shadow-lg border-t border-amber-200"
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.2 }}
          >
            <ul className="px-2 pt-2 pb-3 space-y-1 sm:px-3">
              {menuItems.map((item) => (
                <li key={item.id}>
                  <a
                    href={item.href}
                    className="block px-3 py-2 rounded-md text-base font-medium text-amber-900 hover:bg-amber-50"
                  >
                    {item.text}
                  </a>
                </li>
              ))}
            </ul>
          </motion.div>
        )}
      </AnimatePresence>
    </motion.nav>
  );
};

export default Header;
