import React, { useRef, useState, useEffect } from 'react';
import Logo from '../../assets/images/TİA_LOGO.png';
import { Menu } from 'lucide-react';

const Header = () => {
  const [bgStyle, setBgStyle] = useState({ left: '8px', width: 'calc(100% / 6 - 16px)' });
  const [isScrolled, setIsScrolled] = useState(false);
  const [menuOpen, setMenuOpen] = useState(true);
  const menuRef = useRef(null);

  const menuItems = [
    { id: 1, text: 'Anasayfa' },
    { id: 2, text: 'Hakkımızda' },
    { id: 3, text: 'Galeri' },
    { id: 4, text: 'Sesli Tur' },
    { id: 5, text: 'Quiz' },
    { id: 6, text: 'Rota' },
  ];

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      if (scrollPosition > 50) {
        setIsScrolled(true);
        setMenuOpen(false);
      } else {
        setIsScrolled(false);
        setMenuOpen(true);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleHover = (e) => {
    if (!menuRef.current) return;

    const item = e.target;
    const rect = item.getBoundingClientRect();
    const menuRect = menuRef.current.getBoundingClientRect();

    const width = rect.width;
    const left = rect.left - menuRect.left;

    setBgStyle({
      left: `${left}px`,
      width: `${width}px`
    });
  };

  const handleMouseLeave = () => {
    setBgStyle({ left: '8px', width: 'calc(100% / 6 - 16px)' });
  };

  return (
    <nav className={`w-full fixed top-0 left-0 z-[1000] transition-all duration-300 ease-in-out ${isScrolled ? '' : 'mt-[2%]'}`}>
      <div className="max-w-[1400px] mx-auto px-4">
        <div className="grid grid-cols-[130px_minmax(auto,3fr)_120px] items-center h-16">

          {/* Logo Section */}
          <div className={`flex items-center transition-all duration-500 ${isScrolled ? 'opacity-0 -translate-y-full' : 'opacity-100 translate-y-0'}`}>
            <img src={Logo} className="w-full h-auto" alt="Beşirağa Külliyesi" />
          </div>

          {/* Menu Container */}
          <div className="relative h-full w-full max-w-[800px] mx-auto">
            {isScrolled && (
              <div 
                className="hidden items-center justify-center cursor-pointer text-[#BE1D2D] z-[3] p-2 rounded transition-all duration-300"
                onClick={() => setMenuOpen(!menuOpen)}
                style={{ display: isScrolled ? 'flex' : 'none' }}
              >
                <Menu size={24} className="p-2.5 rounded shadow-[0_5px_15px_rgba(0,0,0,0.15)]" />
              </div>
            )}

            {/* Menu Section */}
            <div 
              ref={menuRef}
              className={`relative flex justify-around items-center rounded-[10px] bg-white shadow-[0_10px_50px_rgba(0,0,0,0.1)] z-[1] overflow-hidden transition-all duration-500 ${
                menuOpen ? 'opacity-100 translate-y-0 h-16' : 'opacity-0 -translate-y-5 h-0'
              }`}
              onMouseLeave={handleMouseLeave}
            >
              {menuItems.map((item, index) => (
                <a
                  key={item.id}
                  href="#"
                  className={`relative font-['Maven_Pro',sans-serif] font-semibold text-[clamp(16px,1.5vw,20px)] no-underline px-[clamp(12px,1.5vw,20px)] py-[clamp(10px,1vw,14px)] rounded-[9px] transition-colors duration-300 z-[2] whitespace-nowrap ${
                    index === 0 ? 'text-white' : 'text-[#9c555c]'
                  } hover:text-white`}
                  onMouseEnter={handleHover}
                >
                  {item.text}
                </a>
              ))}

              {/* Menu Background */}
              <div 
                className="absolute top-1/2 -translate-y-1/2 h-[45px] bg-[#BE1D2D] rounded-[9px] transition-all duration-300 z-0"
                style={bgStyle}
              ></div>
            </div>
          </div>

          {/* Contact Section */}
          <div className={`flex justify-end transition-all duration-500 ${isScrolled ? 'opacity-0 translate-x-full' : 'opacity-100 translate-x-0'}`}>
            <span className="px-[clamp(20px,2vw,40px)] py-[clamp(10px,1vw,15px)] text-[clamp(16px,1.5vw,22px)] rounded-[15px] text-white bg-[#BE1D2D] font-['Maven_Pro',sans-serif] font-bold cursor-pointer transition-colors duration-300 whitespace-nowrap hover:bg-[#a01825]">
              İletişim
            </span>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Header;
