import React, { useRef, useState, useEffect } from 'react';
import { Menu } from 'lucide-react';

const Header = () => {
  const [bgStyle, setBgStyle] = useState({ left: '0px', width: '0px' });
  const [isScrolled, setIsScrolled] = useState(false);
  const [menuOpen, setMenuOpen] = useState(true);
  const menuRef = useRef(null);

  const menuItems = [
    { id: 1, text: 'Anasayfa' },
    { id: 2, text: 'Galeri' },
    { id: 3, text: 'Sesli Tur' },
    { id: 4, text: 'Bilgi Yarışması' },
    { id: 5, text: 'Hakkımızda' }
  ];

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      if (scrollPosition > 50) {
        setIsScrolled(true);
        setMenuOpen(false);
      } else {
        setIsScrolled(false);
        setMenuOpen(true);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleHover = (e) => {
    if (!menuRef.current) return;

    const item = e.target;
    const rect = item.getBoundingClientRect();
    const menuRect = menuRef.current.getBoundingClientRect();

    const width = rect.width;
    const left = rect.left - menuRect.left;

    setBgStyle({
      left: `${left}px`,
      width: `${width}px`
    });
  };

  const handleMouseLeave = () => {
    setBgStyle({ left: '0px', width: '0px' });
  };

  return (
    <nav className={`w-full fixed top-0 left-0 z-[1000] transition-all duration-300 ease-in-out ${isScrolled ? '' : 'mt-[2%]'}`}>
      <div className="max-w-[1200px] mx-auto px-4">
        <div className="flex justify-center items-center h-16">

          {/* Menu Container */}
          <div className="relative h-full w-full max-w-[800px]">
            {isScrolled && (
              <div 
                className="flex items-center justify-center cursor-pointer text-amber-800 z-[3] p-2 rounded transition-all duration-300"
                onClick={() => setMenuOpen(!menuOpen)}
              >
                <div className="p-2.5 rounded shadow-[0_5px_15px_rgba(0,0,0,0.15)] bg-amber-50 border border-amber-200">
                  <Menu size={24} />
                </div>
              </div>
            )}

            <div 
              ref={menuRef}
              className={`relative flex justify-around items-center rounded-[10px] bg-gradient-to-r from-amber-50 to-stone-50 shadow-[0_10px_50px_rgba(120,53,15,0.15)] border border-amber-200/30 z-[1] overflow-hidden transition-all duration-500 ${
                menuOpen ? 'opacity-100 translate-y-0 h-16' : 'opacity-0 -translate-y-5 h-0'
              }`}
              onMouseLeave={handleMouseLeave}
            >
              {menuItems.map((item, index) => (
                <a
                  key={item.id}
                  href="#"
                  className="relative font-['Maven_Pro',sans-serif] font-semibold text-[clamp(16px,1.5vw,20px)] no-underline px-[clamp(16px,2vw,24px)] py-[clamp(10px,1vw,14px)] rounded-[9px] transition-colors duration-300 z-[2] whitespace-nowrap text-amber-900 hover:text-white"
                  onMouseEnter={handleHover}
                >
                  {item.text}
                </a>
              ))}

              <div 
                className="absolute top-1/2 -translate-y-1/2 h-[45px] rounded-[9px] transition-all duration-300 z-0 shadow-lg"
                style={{
                  ...bgStyle,
                  backgroundColor: '#451a03'
                }}
              ></div>
            </div>
          </div>

        </div>
      </div>
    </nav>
  );
};

export default Header;
