import React, { useState } from 'react';

const AboutPage = () => {
  const [activeTab, setActiveTab] = useState('tarihce');

  const buildings = [
    { name: 'Cami', year: '1744', desc: 'Kare planlı, kubbeli mimari' },
    { name: 'Sebil', year: '1744', desc: 'Benzersiz içeri kavisli pencereler' },
    { name: 'Kütüphane', year: '1744', desc: 'Barok süslemeli zengin koleksiyon' },
    { name: 'Medrese', year: '1744', desc: '12 hücreli eğitim merkezi' },
    { name: 'Tekke', year: '1745', desc: 'Nakşibendi tarikatı merkezi' },
  ];

  return (
    <div className="w-full overflow-x-hidden">

      {/* Full Width Hero */}
      <section className="relative h-screen flex items-center justify-center bg-gradient-to-br from-stone-900 via-amber-950 to-stone-900 overflow-hidden">
        {/* Animated Background */}
        <div className="absolute inset-0 opacity-20">
          {[...Array(20)].map((_, i) => (
            <div
              key={i}
              className="absolute bg-amber-500 rounded-full"
              style={{
                width: `${Math.random() * 300 + 50}px`,
                height: `${Math.random() * 300 + 50}px`,
                top: `${Math.random() * 100}%`,
                left: `${Math.random() * 100}%`,
                animation: `float ${Math.random() * 10 + 10}s ease-in-out infinite`,
                animationDelay: `${Math.random() * 5}s`,
                filter: 'blur(40px)',
              }}
            />
          ))}
        </div>

        <div className="relative z-10 text-center px-4">
          <div className="mb-6">
            <span className="text-amber-500 text-6xl md:text-8xl font-bold block mb-2">280</span>
